css:
@-moz-document domain("forum.kerbalspaceprogram.com") {
/*Green Modern KSP Forum by xXRobloxRobloxianXx(KerbalNetwork)*/

/*Header*/
#ipsLayout_header header {
    padding: 5px 0 130px 0;
    margin-bottom: -125px;
    background-color: #35bd72 !important;
}

/*Data Widget Tittle*/
.ipsWidget .ipsDataItem_title {
    font-size: 16px !important;
}

/*Data Tittle*/
.ipsDataItem_title {
    font-size: 17px !important;
    margin: 0 0 5px 0;
    font-weight: 400;
    display: inline-block;
}

#ipsLayout_contentWrapper {
    width: 100%;
    background-color: rgba(45, 165, 77, 0.75) !important;
}

/*Navigation*/
.ipsNavBar_primary {
    position: relative;
    padding: 5px 5px 40px 5px;
    z-index: 2000;
    background: #35bd72 !important;
    border-radius: 2px 2px 0px 0px;
}

/*Button Bar*/
.ipsButtonBar {
    line-height: 1;
    background: rgb(47, 204, 117) !important;
}

/*Body*/
body {
    font-family: "Calibri Light", Avenir Next, Arial, sans-serif !important;
    font-size: 16px !important;
    line-height: 18px;
    color: #35bd72;
    height: 100%;
    background-color: #ebeef2;
}

/*Devblogs*/
.ipsWidget.ipsWidget_vertical .ipsWidget_title {
    padding: 10px;
    margin: 0;
    font-size: 17px !important;
    font-weight: 400;
    position: relative;
    color: #fff !important;
    background: #24c36c !important;
    border-radius: 2px 2px 0px 0px;
}

/*Section Tittle*/
.ipsType_sectionTitle {
    font-size: 17px;
    font-weight: 400;
    padding: 11px 15px 11px 15px;
    margin-top: 0;
    background: #24c36c !important;
    color: #ffffff;
    border-radius: 2px 2px 0px 0px;
}

/*Forums Sign*/
.ipsType_pageTitle {
    font-size: 26px;
    font-weight: 400;
    line-height: 1.2;
    margin: 0;
    color: #fff !important;
}

/*text or icons*/
a {
    color: #426150 !important;
    text-decoration: none;
}

/*logo*/
.ipsItemStatus.ipsItemStatus_large {
    background: #35bd72 !important;
    color: #fff;
    width: 40px;
    height: 40px;
    line-height: 40px;
    border-radius: 20px;
    display: inline-block;
    text-align: center;
    font-size: 18px;
    -webkit-transition: 0.15s all linear;
    -moz-transition: 0.15s all linear;
    -ms-transition: 0.15s all linear;
    -o-transition: 0.15s all linear;
    transition: 0.15s all linear;
}

/*dot*/
.ipsItemStatus:not( .ipsItemStatus_large ) {
    color: #8fd2ac !important;
    font-size: 14px;
    line-height: inherit;
    vertical-align: middle;
}

/*Comment Sign*/
.ipsCommentCount {
    padding: 5px 10px;
    border-radius: 3px;
    background: #35db72 !important;
    display: inline-block;
    font-size: 12px;
    text-transform: uppercase;
    position: relative;
    text-align: center;
    line-height: 15px;
    color: #333;
    margin-bottom: 3px;
}

/*Subtittle*/
ipsType_richText {
    word-wrap: break-word;
    overflow-wrap: break-word;
    line-height: 1.5;
    color: #ffffff !important;
}

/*button*/
.ipsApp .ipsButton_overlaid {
    background: rgba(255, 255, 255, 0.5);
    opacity: 1;
    color: #fff !important;
}

/*Button Primary*/
.ipsApp .ipsButton_primary {
    background: #35bd72 !important;
    color: #ffffff;
}

/*Button Active*/
.ipsButtonRow li > a.ipsButtonRow_active, .ipsButtonRow li > button.ipsButtonRow_active {
    background: #a2ffa9 !important;
    border-color: #323232;
    color: #fff;
    text-shadow: none;
}

/*Start new topic*/
.ipsApp .ipsButton_important {
    background: #35db72 !important;
    color: #ffffff;
}

/*Side Menu Active*/
.ipsSideMenu_itemActive a, a.ipsSideMenu_itemActive, .ipsSideMenu_withChecks .ipsSideMenu_item > input[type="checkbox"]:checked ~ a, .ipsSideMenu_withRadios .ipsSideMenu_item > input[type="radio"]:checked ~ a {
    background: #ffffff !important;
    color: #fff;
}

/*Positive Profile Score*/
.cProfileRepScore_positive {
    background: #35bd72;
    color: #fff !important;
}

/*Profile Stats*/
#elProfileStats {
    background: rgb(86, 189, 133) !important;
    color: #fff;
    height: 65px;
    padding: 12px 12px 12px 155px;
    font-size: 15px;
    font-weight: 500;
    position: relative;
}

/*Time Line*/
html[dir="ltr"] .ipsStream.ipsStream_withTimeline {
    border-left: 3px solid #35bd72 !important;
    margin-left: 20px;
}
/* i really want this to be global */
/*Time Line Round*/
.ipsStreamItem_time {
    display: inline-block;
    border-radius: 20px;
    background: #35bd72 !important;
    color: #fff;
    line-height: 24px;
    font-size: 12px;
    padding: 0 20px;
}

/*App button normal*/
.ipsApp .ipsButton_normal {
    background: #35db72 !important;
    color: #ffffff;
}

/*tabs*/
.ipsTabs {
    background: #35db72 !important;
    padding: 10px 10px 0 10px;
    margin: 0;
    list-style: none;
}

/*fontlarge*/
.ipsType_large {
    font-size: 18px !important;
 }
}
