css:
@-moz-document url-prefix("https://bittrex.com"), url-prefix("https://www.bittrex.com") {
@media (min-width: 1600px) {
	.container {
		width: 100%;
	}
	.container.carousel ul li {
		width: 374px;
	}
	.container.marketing .featurette > div:nth-child(1),
	.container.marketing .featurette > div:nth-child(2) {
		width: 50%;
		float: left;
	}
    .container.carousel .stage {
    	display: none;
	}
    #market-wrapper #rowChart > div {
    	width: 100%;
	}
    #market-wrapper #pad-wrapper .market-stats {
    	width: 100%;
	}
	#market-wrapper #pad-wrapper .market-stats > div {
    	margin-top: 5px;
    	width: 14.28%;
    	float: left;
	}
    #market-wrapper #pad-wrapper .market-stats > div:first-child h1 {
    	font-size: 20px;
   	 	line-height: 50px;
	}
	#market-wrapper #pad-wrapper .market-stats > div:not(:first-child) {
    	margin-top: 15px;
	}
	#market-wrapper #pad-wrapper .market-stats > div img,
	#market-wrapper #pad-wrapper .market-stats > div h1 {
    	padding-top: 2px;
	}
}
