/* update 2017-08-05 experimental */
@media (min-width: 1700px) {
	
	#footer-navigation {
	 bottom: auto;
	 top: 0;
	 left: 20%;
	 width: 46%;
	 font-size: 11px;
	 border: 0;
	}
	#footer-navigation > div > div > ul.nav.navbar-nav.navbar-left {
		display: none;
	}

	#sellOrdersTable > tbody > tr > td:nth-child(2) > div,
	#buyOrdersTable > tbody > tr > td:nth-child(5) > div {
		font-weight: bolder;
	}
	#pad-wrapper > div:nth-child(8),
	#pad-wrapper > div:nth-child(6) {
		position: fixed;
		z-index: 5;
		bottom: -160px;
		left: 0;
		width: 50%;
		min-height: 220px;
		background-color: #212830!important;
		border: 1px solid #394856!important;
		transition: .3s ease-in;
        padding-left: 2px;
        padding-right: 2px;
	}
	#pad-wrapper > div:nth-child(8) {
		left: auto;
		right: 0;
	}
	#pad-wrapper > div:nth-child(8):hover,
    #pad-wrapper > div:nth-child(6):hover {
		bottom: -1px;
	}
	#pad-wrapper > div:nth-child(8) #market-toolbar-order-history,
	#pad-wrapper > div:nth-child(8) #closedMarketOrdersTable,
	#idOrderOpenTable > div > div.datatables-wrapper,
	#idOrderOpenTable > div > div.row > div.col-xs-8 {
		display:none;
	}
	#pad-wrapper > div:nth-child(8):hover #market-toolbar-order-history,
	#pad-wrapper > div:nth-child(8):hover #closedMarketOrdersTable,
	#pad-wrapper > div:nth-child(6):hover #idOrderOpenTable > div > div.datatables-wrapper,
	#pad-wrapper > div:nth-child(6):hover #idOrderOpenTable > div > div.row > div.col-xs-8 {
		display:block;
	}
	#pad-wrapper > div:nth-child(8) #market-toolbar-order-history > a,
	#pad-wrapper > div:nth-child(8) #market-toolbar-order-history > button {
		font-size: 10px;
		margin-left: 5px;
	}
	#pad-wrapper > div:nth-child(8) > div > div.row:nth-child(1) > div:nth-child(1) {
		width: 25%;
	}
	#pad-wrapper > div:nth-child(8) > div > div.row:nth-child(1) > div:nth-child(2) {
		width: 75%;
	}
	#pad-wrapper > div:nth-child(8) #closedMarketOrdersTable > tbody > tr > td:nth-child(1),
	#pad-wrapper > div:nth-child(8) #closedMarketOrdersTable > tbody > tr > td:nth-child(2) {
		font-size: 10px;
	}
    
    #pad-wrapper > div:nth-child(4) {
        width: 60%;
        float: left;
    }
    #pad-wrapper > div:nth-child(5) {
        width: 60%;
        float: left;
    }
    #pad-wrapper > div:nth-child(7) {
        width: 40%;
        float: left;
        margin-top: -64px;
    }
    #pad-wrapper > div:nth-child(7) .right-table-wrapper {
        margin-top: 32px;
    }
    #buyOrdersTable > tbody > tr,
    #sellOrdersTable > tbody > tr,
    #marketHistoryTable2 > tbody > tr {
        height: 24px;
        line-height: 24px;
    }
    #orderSummary > div:nth-child(3) {
    	margin-top: 22px;
    }
}

h1, h2, h3, h4, h5 {
    color: #fff;
}
.menu-wrapper .menu-table tr td a,
.content a {
    color: #ABABAB;
    font-weight: bolder;
}
.menu-wrapper,
.well,
.recent-articles,
.related-articles,
.article-sidebar,
.subtle-wrap,
.bs-callout-warning,
.bs-callout-danger,
.bs-callout-info {
    background-color: #262e38!important;
}
.menu-wrapper input,
.menu-wrapper select,
.form-control {
    background-color: #262e38!important;
    border: 1px solid #3d4a59!important;
    color: #fff;
}
.input-group .input-group-addon {
    background-color: #3d4a59;
    border: 1px solid #3d4a59;
    color: #fff;
}
input[disabled], textarea[disabled], input[readonly], textarea[readonly] {
	color: #aaa !important;
}
.btn-default,
.btn-primary {
    background-color: #394856!important;
    border-color: #3d4a59!important;
    color: #fff;
}
.btn-default:hover,
.btn-primary:hover {
    background-color: #485b6d!important;
    border-color: #3d4a59!important;
    color: #fff;
}
input[type="text"]:focus, input[type="password"]:focus, textarea:focus {
    border-color: #fff;
}
.table-bordered {
    border: 0;
}
.table-bordered>tbody>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>thead>tr>th {
    border: 0;
}
.dyn-td-up,
.dyn-stat-up,
.market-stats .title-up {
    color: #71d358!important;
}
.dyn-td-up:after {
    color: #82f465!important;
}
.dyn-td-down,
.dyn-stat-down,
.market-stats .title-down {
    color: #d15a34!important;
}
.dyn-td-down:after {
    color: #e6643a!important;
}
.dyn-td-up,
.dyn-td-down {
    font-weight: bold;
}
.dyn-tr-add {
    animation:none!important;
    -webkit-animation:none!important;
}
body,
.content,
.panel,
.modal-content {
    background-color: #212830!important;
    color: #fff;
}
.container.carousel ul li:not(:last-child) {
    border-right: 1px solid #404040;
    margin-right: 5px;
}
.connected-carousels .carousel-navigation {
    background-color: #212830!important;
    border: 0;
    box-shadow: none;
}
.connected-carousels .carousel-navigation li img {
    border: 0;
}
.container.marketing tbody td {
    font-size: 12px;
}
.menu-wrapper .menu-table tr td {
    border-color: #485b6d!important;
}
.menu-wrapper .menu-table tr td.even {
    background-color: #212830!important;
}
.menu-wrapper .menu-table tr td.odd,
.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #262e38!important;
}
.menu-wrapper .menu-table tr td.even:hover,
.menu-wrapper .menu-table tr td.odd:hover,
.table-hover tbody tr:hover>td, .table-hover tbody tr:hover>th {
    background-color: #394856!important;
}
.pagination>li>a, .pagination>li>span {
    background-color: #262e38!important;
    border-color: #3d4a59;
    color: #fff;
}
.pagination>.active>a, .pagination>.active>a:focus, .pagination>.active>a:hover, .pagination>.active>span, .pagination>.active>span:focus, .pagination>.active>span:hover {
    background-color: #394856!important;
    border-color: #3d4a59;
}
.pagination>.disabled>a, .pagination>.disabled>a:focus, .pagination>.disabled>a:hover, .pagination>.disabled>span, .pagination>.disabled>span:focus, .pagination>.disabled>span:hover {
    background-color: #262e38!important;
    border-color: #3d4a59;
}
.pagination>li>a:focus, .pagination>li>a:hover, .pagination>li>span:focus, .pagination>li>span:hover {
    background-color: #485b6d!important;
    border-color: #3d4a59!important;
    color: #fff;
}

.panel,
.panel-heading {
    border-radius: 0!important;
    border: 1px solid #394856!important;
}
.panel-heading {
	color: #fff!important;
    background-color: #394856!important;
}
.panel-heading span,
.panel-heading table td {
    color: #fff;
}

.alert {
	opacity: 0.7;
}

#footmainbkg,
.footermlinkl,
.footermlinkr {
    background-color: #202020!important;
}

#balance-wrapper h4 {
    font-weight: bolder;
}

#market-wrapper .market-stats .wrapper {
	border: none;
}
#market-wrapper #pad-wrapper {
    padding: 0 4px!important;
}
#market-wrapper .nav-tabs {
	/*border-bottom: 1px solid #394856!important;*/
    border: 0;
}
#market-wrapper #pad-wrapper #tab-wrapper.nav>li>a {
    padding: 7px!important;
}
#market-wrapper #pad-wrapper #tab-wrapper.nav>li.active>a {
    background-color: #394856!important;
	border: 1px solid #394856!important;
    color: #fff;
}
#market-wrapper .row {
    margin-right:0;
    margin-left:0;
}
#market-wrapper .row > div {
    padding-left: 3px!important;
    padding-right: 3px!important;
}
#market-wrapper #idOrderOpenTable > div {
    padding-left: 0px!important;
    padding-right: 0px!important;
}
#market-wrapper h2 {
    font-size: 13px;
}
#market-wrapper #ifrm {
    resize: vertical;
}
#market-wrapper #pad-wrapper > .row:first-child {
    height: 0px;
    float: right;
}
#market-wrapper #pad-wrapper > .row:first-child div:first-child {
    display: none;
}
#market-wrapper #pad-wrapper h2.section-header {
    margin-top: 5px;
    margin-bottom: 5px;
}
#market-wrapper #pad-wrapper .market-stats .title-none {
    color: #fff;
}
#market-wrapper #pad-wrapper .input-group-addon {
    width: 70px!important;
}
#market-wrapper .chart-wrapper path {
	stroke: white;
}
#market-wrapper .chart-wrapper text {
	fill: white;
}
#market-wrapper .orderbook-selector select {
    background-color: #262e38!important;
    border: 1px solid #3d4a59!important;
    color: #fff;
}

/*chart iframe*/

body.Dark,
.Dark .chartContainer,
.Dark .stx-toolbar,
.Dark .stx-toolbar .stx-btn {
  background: transparent;	
}

.Dark .stx-nav,
.Dark .menuSelect li.stx-menu-divider {
	background: #394856!important;
}


}
