css:
@-moz-document domain("adkami.com") {
section .header h2 {
    color: #d6d6d6;
    position: absolute;
    background-color: rgba(0, 0, 0, .7);
    padding: 1px 2px 2px 2px;
}
section .image-new .description {
    display: block !important;
}
section .image-new {
    float: left;
    height: 100px;
    width: 100%;
    background-position: center;
    display: block;
    background-size: cover;
}

.activebox, .header-profil .activebox {
    display: block;
    cursor: pointer;
    height: 66px;
    width: 80px;
    background-color: #262626 !important;
    padding: 5px;
    text-align: center;
    line-height: 60px;
}

.wallpaper-fiche {
    position: fixed !important;
    width: 100%;
    height: 75%;
    float: left;
    background-size: cover;
    background-position: center;
    filter: blur(5px);
    -webkit-filter: blur(5px);
    -moz-filter: blur(5px);
    -o-filter: blur(5px);
    -ms-filter: blur(5px);
    z-index: 0;
    margin-top: -10px;
}
a,
body,
button,
div,
figure,
form,
h1,
h2,
h3,
h4,
h5,
i,
input,
p,
span {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}
#body {
    background-color: #dcdcdc;
    padding: 80px 0 22px;
}
.col-s-8 {
    width: 76.666666666% !important;
}
.col-s-4 {
    width: 22% !important;
}
}
