css:
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document domain("amoursucre.com"), domain("amordoce.com"), domain("sweetamoris.de"), domain("corazondemelon.es"), domain("flirttistoori.com"), domain("csabitasboljeles.hu"), domain("slodkiflirt.pl"), domain("sweetflirt.ro"), domain("sladkiiflirt.ru"), domain("askito-m.com"), domain("sweetcrush.co.uk"), domain("mycandylove.com") {
/* Esses são os dominios que o style vai pegar, não mexer */
  
/* Fundo da página */   
  
index > .connected {
background-color : #FFFFFF !important;
background-image : url("http://ap.imagensbrasil.org/images/2017/07/22/renders_by_haanakko-d94tfq802d74.png") !important ;
background-repeat : no-repeat !important ;
background-position : center center !important ;
background-attachment : fixed !important ;
}
/* Logo do Amor Doce*/
#site-logo > a > img[src*="/logo-as.png"] {
height : 0 !important ;
width : 0 !important ;
background: url("http://ap.imagensbrasil.org/images/2017/07/22/tumblr_inline_nkic324ugq1t3w6j728222.png") no-repeat !important ;
padding-left : 427px !important ;
padding-top : 142px !important ;   
}

.halo {background-image : url("http://www.amoursucre.com/image/halo.png") !important ;}
/* Info do jogador*/
panel-connected {background : #d5adf1!important ;}
#pseudo-container > .blue {
background : #f2d1f2!important ;
box-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
}
.title-container a {
color : #FFFFFF !important ;
text-shadow : 0 1px 1px rgba(125,125,125,0.75) !important ;
}

#episode-container {background : #d5fbff !important ;}
#episode-container span {color : #ffb0fe !important ;}
#episode-container .number {color : #acf1fd !important ;}
  
.trombone {background-image : url("http://ap.imagensbrasil.org/images/2017/07/22/left.png") !important ;}

#currencies-container .values {
background : #f2dfff !important ;
border : 2px solid #ff5ade !important ;
color : #ffffff !important ;
}
#panel-menu a {
background : #d5fbff !important ;
color : #ffb0fe !important ;
}
#panel-menu a:hover {
background : #f5fbff !important ;
color : #ccefff !important ;
}
navigation-menu > div > .pink, navigation-menu > div > .blue {
background : #ff99ff !important ;
color : #ffffff !important ;
    }
navigation-menu > div > .active, navigation-menu > div > a:hover {
background : #ffd5ff !important ;
color : #ff9dff !important ;
    }
navigation-menu > div > .border {
border : none !important ;
    }
side-links > .link-entry {
background : #f2d1f2 !important ;
    }
}
