css:
@-moz-document domain("drudgereport.com")
{
/* text & background */

html,
body,
tbody,
thead,
th,
tr,
td,
blockquote,
li,
ul,
h1,
h2,
h3,
h4,
h5,
h6,
font,
strong,
p,
form,
footer {
    color: white !important;
    background: #101010 !important;
}
select,
button {
    background: #101010 !important;
}
div,
span,
a {
    background-color: #101010 !important;
}
select,
input,
span,
code,
textarea,
pre,
label,
div,
dd,
option {
    color: white !important;
}

/* links */

a {
    color: #9e7534 !important;
    text-decoration: none;
}
a:visited {
    color: #224375 !important;
}

/* highlight */

textarea,
pre,
input,
code {
    background: #333333 !important;
}
}
