css:
@-moz-document domain("dev.bukkit.org") {
#site div#site-main {
    width: 100%;
    padding-left: 0;
    padding-right: 0;
    padding-top: 0;
    padding-bottom: 20px;
    background: #121313;
}

body {
    color: #49bdba;
    font-size: 13px;
    font-family: "Noto Sans","Helvetica Neue LT Pro","Helvetica Neue","Helvetica",Arial,sans-serif;
}

::selection {
    background: #121313;
    color: #49bdba;
    text-shadow: none;
}

.content-container h1 a, .content-container h1 a:active, .content-container h1 a:visited, .content-container h2 a, .content-container h2 a:active, .content-container h2 a:visited, .content-container h3 a, .content-container h3 a:active, .content-container h3 a:visited, .content-container h4 a, .content-container h4 a:active, .content-container h4 a:visited, .content-container h5 a, .content-container h5 a:active, .content-container h5 a:visited, .content-container h6 a, .content-container h6 a:active, .content-container h6 a:visited {
    color: #49bdba;
}

.project-list-item .details .info.name a {
    color: #49bdba;
}

.project-list-item .details .info.stats {
    font-size: 11px;
    color: #49bdba;
}

#content {
    box-shadow: none;
    background: #FFF;
    color: #49bdba;
    padding: 10px;
}

.b-pagination-a .b-pagination-list>.b-pagination-item>a.s-active, .b-pagination-a .b-pagination-list>.b-pagination-item>a:hover, .b-pagination-a .b-pagination-list>.b-pagination-item>span.s-active, .b-pagination-a .b-pagination-list>.b-pagination-item>span:hover {
    background: #121313;
    color: #49bdba!important;
    border: 1px solid #49bdba;
}

.b-pagination-a .b-pagination-list>.b-pagination-item>a, .b-pagination-a .b-pagination-list>.b-pagination-item>span {
    border-radius: 0;
    background: transparent;
    text-decoration: none;
    position: relative;
    border: 1px solid transparent;
    color: #49bdba!important;
}

input[type="text"], input[type="number"], input[type="search"], input[type="password"], input[type="email"], textarea, select {
    background-color: #121313;
    background: #121313 none;
    border: 1px solid #49bdba;
    border-radius: 4px;
    box-shadow: inset 0 1px 1px rgba(0,0,0,0.075);
    color: #49bdba;
    padding: 4px;
    width: 180px;
    -webkit-transition: .2s linear border,0.2s linear box-shadow;
    -moz-transition: .2s linear border,0.2s linear box-shadow;
    transition: .2s linear border,0.2s linear box-shadow;
}

.p-header-a .searchform .b-search-a input[type="search"], .p-header-a .searchform .b-search-a input[type="text"], .p-header-a .searchform .b-search-a input[type="password"], .p-header-a .searchform .b-search-a input[type="email"] {
    color: #49bdba;
    width: 17px;
    border: 1px solid #49bdba;
    background-color: #121313;
    text-indent: 25px;
    -webkit-transition: width,0.5s,ease;
    -moz-transition: width,0.5s,ease;
    transition: width,0.5s,ease;
    position: relative;
}

.p-nav-a .p-nav-item>a {
    font-weight: 400;
    font-size: 13px;
    font-family: "Noto Sans","Helvetica Neue LT Pro","Helvetica Neue","Helvetica",Arial,sans-serif;
    text-shadow: 0 0 #000;
    box-shadow: none;
    border: 0 solid #f9f9f9;
    color: #49bdba;
    display: block;
    padding: 0 20px 0 20px;
    text-align: center;
    line-height: 50px;
}

.primary-header-wrapper {
    background: rgba(16,18,15,0.8);
    border-bottom: 3px solid #49bdba;
    box-shadow: 0 2px 7px rgba(50,50,50,0.2);
    margin-top: -52px;
}

.body-project-listing .level-categories-nav a {
    color: #49bdba;
    font-weight: bold;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}

.body-project-listing .level-categories-nav.highlight a, .body-project-listing .level-categories-nav:hover a {
    color: #49bdba;
}

.body-project-listing section.level-categories {
    width: 200px;
    float: left;
    border: 3px solid #49bdba;
    border-radius: 3px;
    overflow: hidden;
    background: #121313;
}

.body-project-listing .project-list .listing-body .project-list-item {
    border-bottom: 1px solid #181919;
}

.body-project-listing .project-listing-header {
    margin-bottom: 10px;
    border-bottom: 1px solid #181919;
    position: relative;
    padding-bottom: 4px;
}

.e-avatar16::after, .e-avatar32::after, .e-avatar64::after, .e-avatar128::after, .e-avatar256::after, .listing-attachment .e-thumbnail-container::after {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    content: '';
    border: 1px solid #181919;
}

.e-avatar16, .e-avatar32, .e-avatar64, .e-avatar128, .e-avatar256, .listing-attachment .e-thumbnail-container {
    display: block;
    position: relative;
    background: #121313;
    border: 1px solid #181919;
}

body.body-home .home-call-to-action-container {
    padding: 40px 20px;
    background: #121313;
    border-bottom: 1px solid #181919;
}

body.body-home .primary-content p {
    font-size: 14px;
    color: #49bdba;
}

body.body-home .primary-content h1, body.body-home .primary-content h2, body.body-home .primary-content h3, body.body-home .primary-content h4, body.body-home .primary-content h5, body.body-home .primary-content h6 {
    font-weight: 300;
    color: #49bdba;
    line-height: 1;
}

body.body-home .home-our-project-container {
    padding: 40px 20px 20px 20px;
    background: #181919;
    border-bottom: 1px solid #181919;
}

body.body-home .home-our-project-container .home-our-project li .project-name a {
    color: #49bdba;
}

body.body-home .home-our-project-container .home-our-project li .project-category {
    font-size: 13px;
    color: #49bdba;
    margin-bottom: 4px;
}

body.body-home .home-our-project-container .home-our-project li a.e-project-avatar {
    width: 88px;
    height: 88px;
    overflow: hidden;
    border-radius: 3px;
    display: inline-block;
    position: relative;
    border: 2px solid #49bdba;
    margin: 0 10px 10px 0;
}

body.body-home .home-news-container {
    padding: 40px 20px 240px 20px;
    background: #121313;
    text-align: center;
}

body.body-home .home-news-container .home-news .entry h5 a {
    color: #49bdba;
}

body.body-home .home-news-container .home-news .entry .date {
    color: #49bdba;
    margin-bottom: 4px;
    display: block;
}

#site .atf {
    width: 100%!important;
    max-width: none!important;
    padding: 0!important;
    z-index: 2!important;
    background-color: #121313;
    border-bottom: 1px solid #181919;
}

.atf {
    background: #121313;
    border-bottom-width: 0;
    border-top-width: 0;
    margin: 0;
}

.p-header-b .project-details-container .project-user h1 a, .p-header-b .project-details-container .project-user h1 a:link h1 a:visited, .p-header-b .project-details-container .project-user h1 a:hover, .p-header-b .project-details-container .project-user h1 a:active {
    color: #49bdba;
}

.p-header-b .project-details-container .project-user>h2 a {
    font-size: 14px;
    line-height: 14px;
    color: #49bdba;
}

.e-header-nav .e-wrapper {
    width: 1000px;
    margin-left: auto;
    margin-right: auto;
    border-top: 2px solid #49bdba;
    position: relative;
}

.e-header-nav .e-wrapper .e-menu>li>a, .e-header-nav .e-wrapper .e-menu>li span {
    display: inline-block;
    padding: 16px;
    color: #49bdba;
    position: relative;
}

.e-header-nav .e-wrapper .e-menu>li.e-selected>a::before {
    background: #49bdba;
}

blockquote {
    color: #49bdba;
    border-style: solid;
    border-width: 3px;
    border-color: #181919;
    background: #121313;
    margin: 19.5px 5px 19.5px 5px;
    padding: 12px;
    overflow: hidden;
    text-overflow: ellipsis;
    border-radius: 5px;
}

body.body-project #site div#site-main #content .primary-content blockquote, body.body-projectsettings #site div#site-main #content .primary-content blockquote {
    background: #121313;
}

.user-tag-large {
    position: relative;
    height: 45px;
    width: 275px;
    border: 3px solid #181919;
    border-radius: 4px;
    padding: 4px;
    background-color: #121313;
    font-weight: 400;
}

.cf-sidebar-wrapper .cf-recentfiles .file-tag {
    display: block;
    border: 3px solid #181919;
    background: #121313;
    border-radius: 4px;
    padding: 5px;
    margin-bottom: 5px;
    position: relative;
}

#site table.b-table-a:not(.forum-listing):not(.project-listing):not(.forum-thread-listing):not(.conversation-listing) tr:nth-child(odd), #site .b-shoutbox-a .shoutbox-messages>table.listing:not(.forum-listing):not(.project-listing):not(.forum-thread-listing):not(.conversation-listing) tr:nth-child(odd), .b-shoutbox-a #site .shoutbox-messages>table.listing:not(.forum-listing):not(.project-listing):not(.forum-thread-listing):not(.conversation-listing) tr:nth-child(odd) {
    background: #242626;
}

#site table.b-table-a:not(.forum-listing):not(.project-listing):not(.forum-thread-listing):not(.conversation-listing) tr, #site .b-shoutbox-a .shoutbox-messages>table.listing:not(.forum-listing):not(.project-listing):not(.forum-thread-listing):not(.conversation-listing) tr, .b-shoutbox-a #site .shoutbox-messages>table.listing:not(.forum-listing):not(.project-listing):not(.forum-thread-listing):not(.conversation-listing) tr {
    padding-top: 10px;
    padding-bottom: 10px;
    background: #212323;
}

#site table.b-table-a:not(.forum-listing):not(.project-listing):not(.forum-thread-listing):not(.conversation-listing), #site .b-shoutbox-a .shoutbox-messages>table.listing:not(.forum-listing):not(.project-listing):not(.forum-thread-listing):not(.conversation-listing), .b-shoutbox-a #site .shoutbox-messages>table.listing:not(.forum-listing):not(.project-listing):not(.forum-thread-listing):not(.conversation-listing) {
    border-collapse: separate;
    border-left: 1px solid #2f3333;
    border-right: 1px solid #2f3333;
    border-bottom: 1px solid #2f3333;
}

#site table.b-table-a:not(.forum-listing):not(.project-listing):not(.forum-thread-listing):not(.conversation-listing) td, #site .b-shoutbox-a .shoutbox-messages>table.listing:not(.forum-listing):not(.project-listing):not(.forum-thread-listing):not(.conversation-listing) td, .b-shoutbox-a #site .shoutbox-messages>table.listing:not(.forum-listing):not(.project-listing):not(.forum-thread-listing):not(.conversation-listing) td {
    padding: 5px 10px;
    border: 1px solid #2f3333;
    position: relative;
    border-right: 0;
    border-bottom: 0;
    line-height: 14px;
    background: 0;
}

.b-table-a td, .b-shoutbox-a .shoutbox-messages>table.listing td {
    background: #49bdba;
    box-shadow: inset 0 1px 0 rgba(255,255,255,0.1);
    border: 0;
    border-width: 1px;
    border-top-width: 0;
    padding: 8px 4px;
    vertical-align: middle;
    color: #49bdba;
}

.project-file-listing .project-file-list-item .project-file-game-version .additional-versions {
    height: 20px;
    border: 1px solid #49bdba;
    border-radius: 2px;
    padding: 1px 3px;
    margin-left: 5px;
    background: 0;
    color: #49bdba!important;
    font-size: 12px;
}

body.body-project #site div#site-main #content .primary-content h1, body.body-project #site div#site-main #content .primary-content h2, body.body-project #site div#site-main #content .primary-content h3, body.body-project #site div#site-main #content .primary-content h4, body.body-project #site div#site-main #content .primary-content h5, body.body-project #site div#site-main #content .primary-content h6, body.body-projectsettings #site div#site-main #content .primary-content h1, body.body-projectsettings #site div#site-main #content .primary-content h2, body.body-projectsettings #site div#site-main #content .primary-content h3, body.body-projectsettings #site div#site-main #content .primary-content h4, body.body-projectsettings #site div#site-main #content .primary-content h5, body.body-projectsettings #site div#site-main #content .primary-content h6 {
    font-weight: 300;
    color: #49bdba;
}

body.body-project-filedetails .details-panel .details-content .details-versions ul li {
    display: inline-block;
    padding: 3px 8px;
    margin: 0 2px 5px 0;
    background-color: #121313;
    border-radius: 4px;
    border: 1px solid #49bdba;
}

body.body-project-filedetails .details-panel .details-content .details-changelog .logbox {
    max-height: 300px;
    overflow-x: hidden;
    padding: 20px;
    border: 3px solid #242626;
    background-color: #181919;
    border-radius: 4px;
}

code {
    display: inline!important;
    background: #181919;
    padding: 2px 5px;
}

.listing-attachment>li {
    display: block;
    float: left;
    text-align: left;
    margin: 0 10px 22px;
    overflow: hidden;
    position: relative;
    width: 300px;
    height: 192px;
    border: 1px solid #49bdba;
}

.alert.no-results {
    padding: 14px!important;
    background: #181919!important;
}

#site .alert {
    background-color: #181919;
    background: #181919;
}

body.body-project #site div#site-main #content .no-results, body.body-projectsettings #site div#site-main #content .no-results {
    padding: 3px;
    text-align: center;
    color: #49bdba;
}

.button, button[type="submit"] {
    background-color: #181919;
    background-image: -webkit-linear-gradient(bottom,#181919,#181919);
    background-image: linear-gradient(to top,#181919,#181919);
    box-shadow: none;
    border-radius: 3px;
    border: 1px solid #49bdba;
    color: #49bdba;
    transition: none;
}

.content-container h1, .content-container h2, .content-container h3, .content-container h4, .content-container h5, .content-container h6 {
    color: #49bdba;
}
}
