css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.liveme.com") {
  
body {
	font-family: Gotham-Book,HelveticaRegular,Helvetica,Verdana,Arial,Microsoft YaHei;
	font-size: 16px;
	line-height: 1.1em;
	color: #333;
	background-color: #333;
}  

.main .live_room .live_message .scroll_w {
	padding-right: 4px;
	background: #000;
}
	
.main .live_room .live_self {
	width: 320px;
	height: 85px;
	background: #111;
	margin-bottom: 10px;
	position: relative;
}	
	
.main .live_room .live_message .audience {
	padding: 10px 8px;
	background: #111;
}

.main .live_room .live_message {
	width: 320px;
	height: 100%;
	float: right;
	background: #111;
	position: relative;
	overflow: hidden;
}
	
.main .live_room .live_self .body .location, .main .live_room .live_self .body .nickname {
	height: 20px;
	line-height: 20px;
	margin-left: 50px;
	max-width: 130px;
	overflow: hidden;
	text-overflow: ellipsis;
	white-space: nowrap;
	color: #fff;
}
  
.main .live_room .live_message .messaging {
	padding: 12px 25px 10px 12px;
	min-height: 200px;
	margin-top: 5px;
	overflow: auto;
	overflow-x: hidden;
	color: #d1d1d1;
	background: #111;
}  

.main .live_room .live_message .say {
	padding: 10px 5px 10px 12px;
	background: #111;
}
  
.main .live_room .live_gifts {
	height: 70px;
	background: #111;
	position: relative;
}  

.main .live_room .live_gifts .list {
	padding: 0 0 0 15px;
	float: left;
	position: absolute;
	bottom: 5px;
	background: #111;
	border: 5px;
	margin-right: 160px;
}
  
.main .live_recommend .header {
	padding: 0 5px;
	height: 50px;
	line-height: 50px;
	background: #333;
}  
  
.header .top .action .top_download {
	width: 195px;
	height: 330px;
	background: #111;
	position: absolute;
	top: 64px;
	border-radius: 8px;
	border-top-left-radius: 0;
	border-top-right-radius: 0;
	border-top: 4px solid #8400ff;
	box-shadow: 0 1px 7px rgba(0,0,0,.35);
	left: -20px;
	z-index: 10;
	display: none;
}  
  
.header .top.topbar-scroll-bg, .header .top.topbar-scroll-bg .top {
	background: #000;
}
  
.header .top .nav .link.current::after {
	content: "";
	position: absolute;
	width: 100%;
	bottom: 0;
	border-bottom: 4px solid #8400ff;
	left: 0;
	color: #8400ff;
}  
  
.content .category a.current::after {
	content: "";
	position: absolute;
	width: 100%;
	border-bottom: 4px solid #8400ff;
	bottom: -4px;
	left: 0;
}
	
	
.content .category a.current {
	opacity: 1;
	color: #8400ff;
}	
	
.content .countries .country.current {
	border: 1px solid #8400ff;
	color: #ae75e3;
}	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
}
