css:
@-moz-document domain("wiki.pavlov-vr.com") {
#mw-page-base{
	background-image:none!important;
}
.mw-body a.external:visited{
	color:#fafafa!important;
}
body, .noprint, .mw-body, #left-navigation, #mw-head, code, .thumbinner{
	color:#fafafa!important;
    background-color:#222!important;
}
.selected span a{
	color:#fafafa!important;
    text-shadow: 1px 1px #111;
	background: #038477; /* Old browsers */
	background: -moz-linear-gradient(top,  #038477 0%, #04e8d1 100%); /* FF3.6-15 */
	background: -webkit-linear-gradient(top,  #038477 0%,#04e8d1 100%); /* Chrome10-25,Safari5.1-6 */
	background: linear-gradient(to bottom,  #038477 0%,#04e8d1 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    border-width: 1px;
    border-style: solid;
    border-color: rgb(170, 170, 170);
    border-image: initial;
}
.new span a, #ca-viewsource span a, .collapsible span a, #simpleSearch input{
	color:#fafafa!important;
	background: #222222; /* Old browsers */
	background: -moz-linear-gradient(top,  #222222 0%, #333333 100%); /* FF3.6-15 */
	background: -webkit-linear-gradient(top,  #222222 0%,#333333 100%); /* Chrome10-25,Safari5.1-6 */
	background: linear-gradient(to bottom,  #222222 0%,#333333 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    border-width: 1px;
    border-style: solid;
    border-color: rgb(170, 170, 170);
    border-image: initial;
}
.mw-wiki-logo, .capcontent p{
	filter:invert(100%)!important;
}
.toc, .wikitable, table.wikitable > tr > th, table.wikitable > * > tr > th, #mw-panel{
	color:#fafafa!important;
    background-color:#111!important;
    border-width: 1px;
    border-style: solid;
    border-color: rgb(170, 170, 170);
    border-image: initial;
}
.firstHeading, h2, h3{
	color:#fafafa!important;
}
#simpleSearch #searchInput{
	height:100%!important;
}
a{
	color:#88b2f7!important;
}
a:visited{
	color:#0645ad!important;
}
.capcontent{
	color:#fafafa!important;
    background-color:#07efe4!important;
}
}
