css:
/* i really want this to be global */
.media-box__header {
    position:absolute;
    width: 100%;
    bottom:0px;
}
.media-box__content {
    position:relative;
    top:0px;
}
.media-box__overlay:nth-child(2) {
    top:0;
}
.media-box{
    padding-bottom:22px;
}

/*------------------------------------------------------------*/




.header, .header--secondary, #container, a.header__link, a.header__link:hover, .header__dropdown:hover>a, .header__input, .header__input:focus, select.header__input, select.header__input:focus, .block__content, .block__tab, a.header__search__button, button.header__search__button, span.header__link-user__dropdown-arrow, .media-box__header, span.header__link-user__dropdown-arrow:hover, #imagespns, .image-description, .alternating-color:nth-child(even), .block--fixed, .block__header__dropdown-tab a, .table>tbody>tr:nth-child(even), .header__dropdown:hover span.header__link-user__dropdown-arrow{
    background-color:#000;
}
.block__header, .block__header--sub, .block__header--single-item, .block__header a, .block__header--sub a, .block__header--single-item a, .button, .header--secondary .header__dropdown:hover>a, .table>thead>tr, select.header__input:hover, select.header__input:focus:hover {
    background-color: #1b1b1b;
}
a.block__header--single-item, .block__header a, .block__header--sub a, .block__header--single-item a, a.block__header--single-item:hover, .block__header a:hover, .block__header--sub a:hover, .block__header--single-item a:hover  {
    color: #f90;
}
#footer, a.block__header--single-item:hover, .block__header a:hover, .block__header--sub a:hover, .block__header--single-item a:hover, .input, .block__header__dropdown-tab:hover>a, .alternating-color:nth-child(odd), a.media-box__header--link:hover, .table>tbody>tr:nth-child(odd) {
    background:#151515;
}
a:not(.tag__name):not(.tag__dropdown__link), a:not(.tag__name):not(.tag__dropdown__link):active, a:not(.tag__name):not(.tag__dropdown__link):hover, a:not(.tag__name):visited, .button--link, .button--link:hover, .button--link:active {
    color: #f90;
}
a:not(.tag__name):not(.tag__dropdown__link):hover, .button--link:hover {
    text-decoration:underline;
}
a.interaction--downvote.active, a.interaction--downvote.active:hover, a.interaction--upvote.active, a.interaction--upvote.active:hover, a.interaction--fave.active, a.interaction--fave.active:hover {
    background: #f90;
    color:#1b1b1b;
}
a.interaction--fave:hover, a.interaction--upvote:hover, a.interaction--downvote:hover, a.interaction--comments:hover{
    background: #530;
}
a.interaction--downvote.disabled , a.interaction--downvote.disabled:hover {
    background-color:#1b1b1b;
    color: #888
}
.block__list a.block__list__link.primary, .block__list  {
    background:none;
    border:none;
}
.block__content, .block__tab:not(.hidden), .block__content:first-child, .block__content:last-child, #imagespns, .image-description, .input, .block__header--js-tabbed, .block__header--js-tabbed a:last-child, .block__header--js-tabbed a:first-child, .block__header--js-tabbed a, .button, .block__header--js-tabbed a:hover, .block--fixed, .table, .table>tbody  {
    border-color:#1d1d1d;
}
.block__header--js-tabbed a.selected, .button:hover, .label--primary, .block__header--js-tabbed a.selected:hover {
    background:#333;
    border-color:#444;
}
.media-box  {
    border:none;
    padding:1px;
}
.header__input--search, .header__input--search:focus {
    color: #333;
    background:#fff;
}
.header a:not(.tag__name):not(.tag__dropdown__link) {
    color:#fff;
}
button.header__search__button, a.header__search__button {
    filter: invert(100%);
    color: #000;
    background: #006fff;
}
a.header__link[href="/"]{
    font-size:0;
}
a.header__link[href="/"]:before{
    font-size: 16px;
    padding-top:10px;
    display:inline-block;
    content:"Derpi";
    width:auto;
    color:#fff;
    font-weight:700;
}
a.header__link[href="/"]:after{
    font-weight:700;
    font-size: 16px;
    position:relative;
    top:1px;
    display:inline-block;
    height:16px;
    padding:5px;
    background:#f90;
    color:#000;
    line-height:16px;
    border-radius:2px;
    content:"hub";
    width:auto;
}
.block--warning {
	background:#1f1209;
    border:none;
}
