css:
@-moz-document domain('9anime.to'){
  #hotest
  {
    display: none;
  }

  .alert.alert-warning
  {
    display: none;
  }

  .home-socials
  {
    display: none;
  }

  .alert-primary
  {
    display: none;
  }

  .mt20
  {
    margin-top: 5px;
  }

  .mt10
  {
    margin-top: 0px;
  }

  .list-link.watchlist .links .item .thumb
  {
    width: 60px;
    height: 80px;
  }

  .list-link .links .item a
  {
    color: #9a9a9a;
    font-weight: 500;
    font-size: 1.3rem;
  }

  .notification
  {
    display: none;
  }

  #nav
  {
    display: block;
    background: #1e1c25;
    margin-bottom: 5px;
  }

  #header #logo a
  {
    display: none;
  }

  #header
  {
    display: block;
    padding-top: 10px;
    height: 60px;
    background: #16151c;
  }

  .ads, .col-xs-24, .col-lg-7.col-sm-24.sidebar, .item.mbtn.toggler.resize, .notification, .col-md-7, .alert.alert-primary.notice, .home-socials, .sticky.sticky-left, .sticky.sticky-right
  {
    display: none!important;
  }

  .col-lg-17, .col-md-17
  {
    width: 100%;
  }

  .watchpage #player
  {
    height: 642px!important;
  }

  #nav #search input, body, html {
      font-family: Verdana !important;
      font-size: 14px !important;
  }
}
