css:
@-moz-document domain("fluentcloud.com"){
/* styles for the login page */

.sign-in-page .sign-in-bg {
    background-image: url(https://www.tellmeagain.xyz/freebies/waifu-mixtape-pink-2.png);
    background-size: cover;
    background-repeat: no-repeat;
    -webkit-filter: blur(0px);
}
.sign-in-page .sign-in {
    background-color: rgba(255, 255, 255, 0.6);
    border-radius: 5px;
    left: 14%;
    top: 75%;
    position: absolute;
}
.sign-in-page .sign-in header {
    background-color: rgba(255, 255, 255, 0.6);
    border-radius: 5px 5px 0px 0px;
    color: #2d2d2d;
}
.sign-in-page .sign-in header h1 {
    color: #2d2d2d;
}
.sign-in-page form button {
    background: #2d2d2d;
    color: #f8f8f8;
    border-radius: 2px;
}
.sign-in-page form button:hover {
    background: #494848;
    color: #ffffff;
    border-radius: 3px;
}
/* END Styles for the login page */

h1, .h1, h2, .h2, h3, .h3, h4, .h4, h5, .h5{
    color:#f8f8f8;
}
p, .p, li{
    color:#f8f8f8;
}
/***** Start styles for the main top nav bars ****/

.navbar.navbar-inverse.navbar-default {
    height: 80px;
    background-color: transparent;
    color: #FF9A9A;
}
.navbar-collapse.collapse.cust-subnav {
    background-color: transparent;
    height: 80px;
    color: #FF9A9A;
}
.cust-navbar-header {
    height: 80px;
}
.cust-navbar-header {
    background-color: transparent !important;
}
.navbar-inverse .navbar-nav li a {
    color: #f8f8f8;
}
.navbar-inverse .navbar-nav>li>a:hover,
.navbar-inverse .navbar-nav>li>a:focus {
    color: #454545;
}
/* top menu bar when a menu is clicked open */

.navbar-inverse .navbar-nav>.open>a,
.navbar-inverse .navbar-nav>.open>a:hover,
.navbar-inverse .navbar-nav>.open>a:focus,
.navbar-nav>#adminhelper>.open> a:active {
    background-color: #fdd3d3;
    color: #f8f8f8;
}
/* top bar drop down menu items hover background color*/

.dropdown-menu li.fc_header a:hover {
    background-color: #ffebeb;
}
/*color of the text in the top bar drop down menu during hover */

.dropdown-menu li.fc_header a:hover {
    color: #454545;
}
.dropdown-menu {
    background-color: #f8f8f8;
}
.dropdown-menu li.fc_header a {
    color: #2d2d2d;
}
/* SuperDuperAdmin exlusive menus and icon */

.navbar-nav>li>li>.dropdown-menu {
    background-color: #f8f8f8;
    padding: 0px;
}
.dropdown-menu>.cust-nav-font-white {
    background-color: #f8f8f8;
    color: #2c2c2c;
}
.dropdown-menu>.cust-nav-font-white:hover {
    background-color: #ffebeb;
    color: #454545;
}
.navbar-inverse .navbar-nav>.open>a,
.navbar-inverse .navbar-nav>.open>a:hover,
.navbar-inverse .navbar-nav>.open>a:focus,
.navbar-nav>#adminhelper>.open> a {
    background-color: #f8f8f8;
    color: #2c2c2c;
}
/* normal and hover colors for icons next to each drop down menu item */

.navbar-nav>li>.dropdown-menu i {
    color: #2c2c2c;
}
.navbar-nav>li>.dropdown-menu i:hover {
    color: #f8f8f8;
}
/* end icons color*/
/* style for user sub menu and it's dropdowns == Also affects extensions dropdown list on right side of nav*/

.cust-subnav ul li ul.dropdown-menu li a {
    background-color: #f8f8f8;
    color: #2c2c2c;
}
.cust-subnav ul li ul.dropdown-menu li a:hover {
    background-color: #ffebeb;
    color: #2c2c2c;
}
.dropdown-menu.cust-subnav ul li ul.dropdown-menu li a:hover {
    background-color: #ffebeb;
    color: #454545;
}
.cust-subnav ul li ul.dropdown-menu {
    background-color: #f8f8f8;
}
.nav li .fa {
    color: #f8f8f8;
    /* icons next to main drop down menu text */
}
.nav li .fa:active,
.nav li .fa:focus {
    color: #2d2d2d;
}
.fa-gear:before:focus,
.fa-cog:before:focus {
    color: #2d2d2d
}
/* END styles for sub bar navigation and menus */
/* call recording icon in sub nav bar*/

.fa-circle:before {
    color: #2c2c2c;
}
/* voicemail drop icon in sub nav bar */

.fa-chevron-circle-down:before {
    color: #2c2c2c;
}
/* fluentcloud and user portal buttons text colors and bg */

.navbar-inverse .navbar-brand {
    color: #f8f8f8;
}
.navbar-inverse .navbar-brand:hover,
.navbar-inverse .navbar-brand:focus {
    background-color: #f8f8f8;
    color: #2d2d2d;
}
/**** END nav bar styles *****/
/* Waifu-materiao main bg << CAN BE CHANGED TO ANY IMAGE >> */

.mfc-container {
    z-index: 1;
    background-image: url(https://www.tellmeagain.xyz/freebies/waifu-mixtape-pink-2.png);
    background-attachment: fixed;
    background-repeat: no-repeat;
    background-size: cover;
    background-color: #f8f8f8;
}
/* fix fow when modal popups come in. This theme is interfering with the function. This will place the transparent layer below the modal so that it can be used correctly. */

.modal-backdrop.in {
    z-index: -1;
}
/*Side Menu items style == like in Extensions left side menu */

.fc_extension a:hover {
    color: #f8f8f8;
}
/* side menu non-hover text */

.nav-pills.nav-stacked>li>a {
    color: #f8f8f8;
}
.nav-pills.nav-stacked>li>a:hover {
    color: #2d2d2d;
    font-weight: 400;
    background-color: #ffebeb;
}
.nav-pills>li.active>a{
 	background-color:#ffebeb!important;
    color:#2d2d2d!important;
}
/* Dashboard menu fix */

.bootstrap-select.btn-group .dropdown-menu li a {
    color: #FF9A9A;
}
/* side bar text in the #phonenumbers page*/

.nav-pills.nav-stacked>li>a.muted {
    color: #6e6e6e;
}
.nav-pills.nav-stacked>li>a.muted:hover {
    color: #454545;
}
.copy-link {
    color: #6e6e6e;
}
.label-success {
    background-color: #f8f8f8;
    color: #454545;
}
.label-primary {
    background-color: #f8f8f8;
    color: #454545;
}
/* large section titles */

.help-title {
    color: #2c2c2c;
}
/* large section titles sub-description */

.lead,
.jumbotron p {
    color: #f8f8f8;
}
/* paragraph headers*/

.col-xs-12 .col-md-6 h2 {
    color: #f8f8f8;
}
h1.help-title {
    color: #f8f8f8;
}
.fs-legend, legend {
    color: #f8f8f8;
}

.col-xs-12.col-sm-11.col-sm-offset-1 h1 {
    color: #f8f8f8;
}
.col-md-10 h1 {
    color: #f8f8f8;
}
/* paragraph text */

.backgrid td {
    color: #f8f8f8;
}
.col-xs-12.col-sm-6.cust-call-hist-dropdown > span {
    color: #f8f8f8;
}
#showRecords {
    color: #2d2d2d;
}
.form-group label,
.controls label {
    color: #f8f8f8;
}
.form-group h1, .form-group h2{
 color:#f8f8f8;   
}
.pager li {
    color: #f8f8f8;
}
.description{
    color:#f8f8f8;
}
.text-center p{
    color:#f8f8f8;
}
.col-sm-12 p, .col-sm-12 span{
    color:#f8f8f8;
}
.col-sm-12 a{
    color:#f8f8f8;
    text-decoration:underline;
}
.col-sm-12 h4{
    color:#f8f8f8;
}
.col-sm-12 a:hover{
    color:#ffebeb;
}

.newquickLink{
    color:#f8f8f8;
}
.showoptions{
    color:#ffffff;
    text-decoration:underline;
}
.showoptions:hover{
color:#ffebeb;
}
#voicemails td{
    color:#f8f8f8;
}
.table-bordered th{
    color:#f8f8f8;
}
.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th{
    color:#2d2d2d;
}
.table-striped>tbody>tr:nth-child(odd)>td a, .table-striped>tbody>tr:nth-child(odd)>th a{
    color:#2d2d2d;
}
.table-striped>tbody>tr:nth-child(even)>td, .table-striped>tbody>tr:nth-child(even)>th{
    color:#f8f8f8;
}
.table-striped>tbody>tr:nth-child(even)>td a, .table-striped>tbody>tr:nth-child(even)>th a{
    color:#f8f8f8;
}
#previousPage a span, #nextPage a span{
    color:#2d2d2d;
}
#meTab label, #faxTab label, #permissionsTab label, #billingTab label{
    color:#f8f8f8;
}
#permissionsTab .row #allowInternalDialCont > label, #permissionsTab .row #allowExternalDialCont > label, #permissionsTab .row #allowInternationalDialCont > label, #permissionsTab .row #allowEmergencyDialCont > label,#permissionsTab .row #allowOverrideDialCont > label, #permissionsTab .row #allowListenWhisperBargeCont > label{
	color:#2d2d2d;
}
#advancedTab .row #allowVirtualUseInputCont > label, #advancedTab .row #qualifyCont > label, #advancedTab .row #encryptionCont > label, #advancedTab .row #voicemailDropCont > label,#advancedTab .row #matchCallerIDCont > label{
    color:#2d2d2d!important;
}
#virtualPassword{
 	color:#2d2d2d;   
}
#advancedTab label{
    color:#f8f8f8;
}
#meTab small{
    color:#ffebeb;
}
#advancedTab p, #editHistoryTab p{
    color:#2d2d2d;
}
#advancedTab .help-block{
 	color:#f8f8f8;   
}

#quickGlanceDataCont dt, #quickGlanceDataCont dd{
    color:#f8f8f8;
}
/* side bar in admin dashboard headers lines */

.newquickLinkHeader {
    background-color: #f8f8f8;
    color: #2c2c2c;
}
/* colors for tabs in various pages */

.nav>li>a:hover {
    background-color: #ffebeb;
    color: #cc2f2f;
}
/* ***** BUTTONS BUTTONS BUTTONS == These are buttons that are NOT in the menus */
/* submit or update settings buttons */

.btn-primary {
    background-color: #faa5a9;
    color: #2c2c2c;
    text-decoration:none!important;
    -webkit-transition: .2s all ease-in-out;
-moz-transition: .2s all ease-in-out;
-transition: .2s all ease-in-out;
}
.btn-primary:hover {
    background-color: #f8f8f8;
    border-color: #2c2c2c;
    color: #2c2c2c !important;
}
.btn.btn-danger {
    background-color: #2d2d2d;
    border-color: #2d2d2d;
    color: #f8f8f8;
}
.btn.btn-danger:hover,
.btn.btn-danger:focus,
.btn.btn-danger:active {
    background-color: #faa5a9;
    border-color: #cf8989;
    color: #f8f8f8;
}
#reboot{
    background-color:#f8f8f8;
    color:#2d2d2d;
}
#reboot:hover{
    background-color:#ffebeb;
    color:#2d2d2d;
}
/* ***create new*** button */

.btn.btn-xs.btn-info.createNew {
    background-color: #f8f8f8;
    border-color: #f8f8f8;
    color: #2d2d2d;
}
.btn.btn-xs.btn-info.createNew:hover {
    background-color: #ffebeb;
    border-color: #ffebeb;
    color: #2d2d2d;
}
/* **how to** setup buttons */

.btn-success {
    background-color: #f8f8f8;
    color: #2c2c2c;
    border-color: #f8f8f8;
}
.btn-success:hover {
    background-color: #f8f8f8;
    color: #f8f8f8;
    border-color: #cf8989;
}
/* extensions button on top of the list in main pane */

.button-switch input:checked + label,
.button-switch button:active {
    background-color: #cc2f2f;
    color: #f8f8f8;
}
.button-switch label,
.button-switch button {
    background-color: #fdd3d3;
    color: #2c2c2c;
}
.button-switch label:hover,
.button-switch button:hover {
    background-color: #ffebeb;
    color: #454545;
    box-shadow: none;
}
/* dropdown menu button in #routes/ConferenceBridge page */

.dropdown-menu>li>a:hover {
    color: #454545;
    background-color: #ffebeb;
}
.btn-default {
    background-color: #f8f8f8;
    border-color: #f8f8f8;
    color: #2d2d2d;
}
.btn-default:hover,
.btn-default:focus,
.btn-default:active,
.btn-default.active,
.open .dropdown-toggle.btn-default {
    background-color: #ffebeb;
    border-color: #ffebeb;
    color: #2d2d2d;
}
.toggle-switch input:checked + label {
    background-color: #ffebeb;
}
.toggle-switch label{
    color:#2d2d2d;
}
/* Learn More Buttons */

.btn.btn-xs.btn-info {
    background-color: #f8f8f8;
    border-color: #f8f8f8;
    color: #2d2d2d;
}
.btn.btn-xs.btn-info:hover {
    background-color: #ffebeb;
    border-color: #f8f8f8;
    color: #2d2d2d;
}

.icon-plus-sign:before{
 color:#2d2d2d;
}
/* dashboards widget dropdown menus */

.bootstrap-select.btn-group .dropdown-menu li {
    background-color: #f8f8f8;
}
.bootstrap-select.btn-group .dropdown-menu li:hover {
    background-color: #ffebeb;
}
.bootstrap-select.btn-group .dropdown-menu li a {
    color: #2c2c2c;
}
.bootstrap-select.btn-group .dropdown-menu li a:hover {
    color: #ffebeb;
}
/* ****Learn More*** Button on homepage */

#startUpgrading,
#startUpgrading a {
    background-color: #cc2f2f;
    border-color: #cc2f2f;
}
#startUpgrading:hover {
    background-color: #ffebeb;
    border-color: #ffe3d3;
}
#startUpgrading:hover > a {
    background-color: #ffebeb;
    color: #454545;
}
/*custom backround actions--Get rid of bg color*/
.cust-actions-background{
    background-color:transparent;
    border:none;
}
}
