css:
@-moz-document domain("workflowy.com") {
.contentTag[title="Filter #qq"] {
    font-size: 1px;
    line-height: 1px;
    display: inline;
    opacity: 0;
}

.qq-proj {
    color: /*[[qq_color]]*/;
    font-style: italic;
    position: relative;
}

.qq-proj > .name:first-child > .bullet {
    visibility: hidden
}

.qq-proj::before, .qq-proj::after {
    position: absolute;
    font-size: 300%;
    z-index: 10000;
    font-family: "Liberation Serif", "Droid Serif", "Georgia";
    font-weight: bold;
}
.qq-proj::before {
    content: '\201c';
}
.qq-proj::after {
    content: '\201d';
    right: -5px;
    bottom: -18px;
}

/***************/

.contentTag[title="Filter #sq"] {
    font-size: 1px;
    line-height: 1px;
    display: inline;
    opacity: 0;
}
.sq-proj > .name > .content {
    color: /*[[sq_color]]*/;
    font-style: italic;
    position: relative;
}

.sq-proj > .name:first-child > .bullet {
    visibility: hidden
}

.sq-proj > .name > .content::before,
.sq-proj > .name > .content::after {
    position: absolute;
    font-size: 300%;
    z-index: 10000;
    font-family: "Liberation Serif", "Droid Serif", "Georgia";
    font-weight: bold;
}
.sq-proj > .name > .content::before {
    content: '\201c';
    left: -25px;
    top: 10px
}
.sq-proj > .name > .content::after {
    content: '\201d';
    right: -10px;
    bottom: -10px;
}
}
