css:
@-moz-document url-prefix("https://vk.com") {
/*
        _         _                  _                         _                 
       | |       | |                | |                       | |                
   __ _| |__  ___| |_ _ __ __ _  ___| |_   _ __ ___   ___   __| | ___ _ __ _ __  
  / _` | '_ \/ __| __| '__/ _` |/ __| __| | '_ ` _ \ / _ \ / _` |/ _ \ '__| '_ \ 
 | (_| | |_) \__ \ |_| | | (_| | (__| |_  | | | | | | (_) | (_| |  __/ |  | | | |
  \__,_|_.__/|___/\__|_|  \__,_|\___|\__| |_| |_| |_|\___/ \__,_|\___|_|  |_| |_|
 

Version: 6.2.8
Created by: VK Material Design Team (Vitaliy Koreev) - https://vk.com/abstractmodern
Modified by: Delmor_S (Sergey Dudkin)
*/

:root {
    --coloursync: #34495E; /* 34495E */
    --blueVKcolor: #082747; /* 1D3853 */
    --darkGrayColor: #151515; /* 151515 */
    --audioDarkGrayColor: #252525; /* 252525 */
    --greenFontColor: #9DFCD0; /* 9DFCD0 */
    --borderColor: rgba(0, 0, 0, 0.15);
}

/* GLOBALS */
div:not(.poster__text):not(.image-search-item),
body,
b,
button,
input,
span,
h1,
h2,
h3,
h4,
h5,
.page_name,
h2.page_name {
    color: #FFF !important;
}
a {
    color: var(--greenFontColor) !important
}

/********** NEWS FEED **********/
.feed_row {
    background-color: rgba(0, 0, 0, 0) !important;
}

/********** CUSTOM BACKGROUND ***********/
.article_layer,
body[onresize],
#page_header_cont .back:after,
#page_header_cont .back:before,
#page_header_cont:before,
#page_layout:before {
    background-image: url(/*[[backgroundImage]]*/) !important;
    background-position: top center !important;
    background-attachment: fixed !important;
    background-size: cover !important;
}
body {
    font-family: Segoe UI, Open Sans, Helvetica Neue, sans-serif;
    background-color: var(--coloursync) !important;
}

/********** BUTTONS ***********/
.audio_new_year_promo .audio_promo__button {
    background-color: var(--audioDarkGrayColor) !important;
}

/********** ARTICLES ***********/
.article.article_view {
    background-color: rgba(0, 0, 0, 0.5);
    padding: 50px;
    border-radius: 20px;
}
.article_layer .article_layer__footer {
    padding-left: 20px;
    padding-right: 20px;
    border-radius: 10px;
    background-color: rgba(0, 0, 0, 0.5);
}

#page_body {
    background-color: transparent !important;
}
.page_block {
    position: relative;
    background-color: #FFF;
    border-radius: 0px !important;
    box-shadow: 0 1px 0 0 #FFF, 0 0 0 1px #FFF;
    margin: 0px 0 0;
    border: solid #ebedf0;
    border-width: 1px 0 0
}
.chat_tab_typing_wrap {
    background-color: var(--blueVKcolor) !important;
}
.article_layer .article_layer__content_footer {
    background-color: transparent;
}

/********** ERROR ***********/
.error {
    background-color: #952F31 !important;
}

/********** AUDIO PLAYER ***********/
.audio_page_player2 .audio_page_player_btn {
    filter: brightness(0) invert(1);
}
.audio_page_player_btn_enabled .btn_icon {
    opacity: 1 !important;
}
.audio_page__shuffle_all .audio_page__shuffle_all_button:hover {
    background-color: rgba(0, 0, 0, 0.5) !important;
}
.audio_page__shuffle_all .audio_page__shuffle_all_button::before {
    filter: brightness(0) invert(1) !important;
}
.audio_pl_snippet {
    background-color: var(--audioDarkGrayColor) !important;
    border-color: black !important;
}
.audio_page_player.audio_page_player_fixed {
    background-color: var(--blueVKcolor) !important;
}
.audio_row .audio_row_inner,
.audio_row .audio_row_inner:hover {
    background-color: rgba(0, 0, 0, 0);
}
.eltt .audio_page_player {
    border-bottom: 0px !important;
    height: 88px !important;
    background-color: var(--audioDarkGrayColor) !important;
}
.audio_row .audio_row_content {
    padding-bottom: 0px !important;
}
.audio_row.audio_row__current .audio_row_content {
    background-color: rgba(0, 0, 0, 0.5)
}
.audio_row.audio_row__current .audio_row_content:hover {
    background-color: transparent !important;
    transition: 300ms !important;
}
.audio_row:hover:not(.audio_row__current) .audio_row_content {
    background-color: rgba(0, 0, 0, 0) !important; /* Transparent */
}
.audio_row:not(.audio_row_current):hover {
    background-color: var(--darkGrayColor) !important;
}
.audio_row.audio_row_with_cover {
    background-color: transparent !important;
}
.audio_layer_container .audio_friend:hover {
    background-color: var(--audioDarkGrayColor) !important;
}
.top_audio_player {
    height: 46px
}
.audio_page_player2.audio_page_player_fixed,
.audio_layer_container .audio_page_player_wrap {
    background-color: var(--audioDarkGrayColor);
}
.audio_pl_snippet2 {
    background-color: var(--audioDarkGrayColor);
}
.audio_page_player2 .audio_page_player_play .icon,
.audio_page_player2 .audio_page_player_prev .icon,
.audio_page_player2 .audio_page_player_next .icon {
    background-color: transparent !important;
    filter: brightness(0) invert(1) !important;
}
.audio_page_layout .audio_search_wrapper {
    background-color: transparent !important;
}
.audio_subscribe_promo__text {
    color: white !important;
}
.audio_page_layout .has_friends_block .audio_page__rows_wrap {
    border-right-color: transparent !important;
}
.audio_page_layout .audio_block:before,
.page_block._audio_page_content_block:before {
    box-shadow: 0 1px 0 0 transparent, 0 0 0 1px transparent !important;
}
.CatalogBlock--divided, .CatalogSection {
    background-color: transparent !important;
}
.CatalogBlock--divided::before, .CatalogSection::before {
    box-shadow: none !important;
}


/* GIF */
.box_title_wrap.box_white {
    background-color: #222;
}


/********** BOTTOM RIGHT FRIENDS **********/
#fc_contacts, .fc_content {
    background-color: black;   
}

input#fc_clist_filter {
    background-color: transparent !important;
}

/********** TOP NOTIFICATIONS **********/
.top_nav_btn#top_notify_btn .top_nav_btn_icon,
.top_nav_btn#top_audio .top_nav_btn_icon {
    filter: brightness(0) invert(1);
}

.tt_w.top_notify_tt {
    background-color: #1A1A1A !important;
    border-color: black !important;
}


.audio_page_player .audio_page_player__cover {
    width: 88px !important;
    height: 88px !important;
}
div.blog_about_company_stats,
div.blog_about_press {
    background-color: rgba(0, 0, 0, 0.5) !important;
}
ul.ui_tabs.clear_fix.blog_about_tabs.page_header_wrap::before {
    display: none !important;
}
#profile_groups_link:hover {
    background-color: rgba(0, 0, 0, 0.5) !important;
}
.im-create .im-creation--item.im-creation--item_hovered,
.im-create .im-creation--item:hover {
    background-color: rgba(0, 0, 0, 0.5) !important;
    border-top: black !important;
}
#top_profile_menu,
#top_notify_wrap,
.eltt,
.chat_onl_inner,
.ms_items_more,
.emoji_tt_wrap,
.tt_default,
.tt_default_right,
.ui_actions_menu,
.tt_w.like_tt,
.tt_w.poll_tt,
.page_status_editor .editor {
    border: 0px solid #c5d0db;
    border-radius: 0px;
    box-shadow: 0 0 23px 12px rgba(0, 0, 0, 0.1)
}
.wide_column .topics_module .topic_title {
    color: var(--blueVKcolor)
}
::selection {
    background: var(--blueVKcolor) !important;
    color: #FFF !important
}
::-moz-selection {
    background: var(--blueVKcolor) !important;
    color: #FFF !important
}

.im-mess.im-mess_selected:not(.im-mess_is_editing), 
.im-mess.im-mess_selected:not(.im-mess_is_editing):hover {
    background-color: var(--darkGrayColor) !important;
    border-radius: 3px 3px 3px 3px !important;
}


/********** DISLIKE **********/
.like_btns .post_dislike .post_dislike_icon {
    filter: brightness(0) invert(1);
}

.wall_module .post_dislike:hover {
    background-color: rgba(0, 0, 0, 0);
}


/********** SCROLLBAR **********/
*::-webkit-scrollbar {
    width: 12px !important;
    height: 12px !important;
    background: var(--blueVKcolor) !important
}
*::-webkit-scrollbar-thumb {
    min-height: 30px !important;
    background: #38699A !important
}


/********** FORWARD MESSAGE **********/
.PopupHeader {
    background-color: var(--darkGrayColor);
}

.MessageForward__content {
    background-color: var(--audioDarkGrayColor);
}

.ListItem--active {
    background-color: var(--darkGrayColor);
}


/********** ADVERTISEMENT **********/
#ads_left {
    display: none !important
}


/********** LOGO **********/
.top_home_link .top_home_logo {
    background: url(https://i.imgur.com/JPBSvic.png) no-repeat;
    height: 46px !important;
    width: 160px !important;
    background-size: auto 22px !important;
}


/********** TOP PANEL **********/
.notify_sources_tt_content .content:not(:hover) .line_cell.selected,
.notify_sources_tt_content .line_cell:hover {
    background-color: #1c1c1c;
}
.notifier_baloon {
    color: #FFF;
    background: rgba(0, 0, 0, 0.66);
    border-radius: 0px;
    box-shadow: 0 0 0 rgba(0, 0, 0, 0)
}
.notifier_baloon_wrap:last-child {
    padding-bottom: 0
}
.notifier_baloon_wrap {
    padding: 0 0 0px 0px
}
.ui_progress .ui_progress_bar {
    background-image: none;
    background: var(--blueVKcolor)
}
.im-page {
    padding-bottom: 17px
}
.fc_msgs a {
    color: #FFF
}
a.top_home_link.fl_l {
    width: 164px
}
#page_header,
#page_header_cont,
#page_header_cont .back,
.top_nav_btn,
.top_nav_link {
    height: 46px !important
}
.top_nav_btn .top_nav_btn_icon {
    margin: 13px auto
}
.top_audio_player .top_audio_player_btn {
    padding: 18px 4px 14px !important;
}
.top_audio_player .top_audio_player_play {
    padding: 16px 4px !important;
}
.top_audio_player .top_audio_player_title_wrap {
    margin: 15px 0
}
.top_nav_link {
    line-height: 46px
}

.chat_onl_inner {
    background-color: rgba(0, 0, 0, .5) !important
}
#top_notify_wrap {
    top: 46px !important;
    left: 0px
}
.top_profile_img {
    width: 46px;
    height: 46px;
    margin: 0px 0
}
#page_header_cont .back {
    background: var(--blueVKcolor);
    border-bottom: 0px
}
.top_audio_player .top_audio_player_title,
.top_profile_name {
    color: #FFF;
    font-weight: 400
}
.top_nav_link.active,
.top_nav_link:hover,
.top_audio_player:hover,
.top_nav_btn:hover,
.audio_row:not(.audio_row_current):hover,
.top_nav_btn.active {
    background-color: rgba(0, 0, 0, 0.2);
    transition: 300ms;
}
.top_nav_link,
.top_audio_player,
.top_nav_btn,
.audio_row:not(.audio_row_current) {
    transition: 300ms
}
.top_audio_player .top_audio_player_btn>div {
    opacity: 1 !important
}
.top_profile_img {
    right: 0;
    position: absolute
}
.top_profile_arrow {
    margin: 22px 48px 0 7px
}
#top_profile_menu:after,
#top_profile_menu:before {
    right: 14px
}
#top_profile_menu:before {
    border-width: 6px;
    margin: 0 -6px;
    border-bottom-color: rgba(0, 0, 0, 0)
}
.chat_tab_img {
    width: 40px;
    height: 40px;
    border-radius: 0
}
.chat_onl_cont {
    padding: 0px 0
}
.chat_onl_inner {
    background-color: #FFF
}
.left_count_wrap,
.left_count_wrap.left_count_wrap_hovered:hover,
a:hover .left_count_wrap,
span.lnk_mail_title.a.clear_fix {
    color: var(--blueVKcolor);
    background: none
}
#side_bar .left_label {
    font-size: 13px;
    color: var(--blueVKcolor)
}





.nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--unread,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--unread {
    color: #FFF
}
nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--photo,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--photo {
    border-color: var(--blueVKcolor)
}
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected .nim-dialog--photo .online.mobile:after,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected .nim-dialog--photo .online.mobile:after,
.ui_tabs.ui_tabs_sliding .ui_tabs_slider {
    background-color: var(--darkGrayColor)
}
.nim-dialog {
    padding: 0 0 0 10px
}
.nim-dialog.nim-dialog_muted.nim-dialog_selected,
.nim-dialog.nim-dialog_selected,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_muted.nim-dialog_selected,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected {
    background: var(--blueVKcolor)
}
.nim-dialog.nim-dialog.nim-dialog_selected.nim-dialog_hovered,
.nim-dialog.nim-dialog.nim-dialog_selected:hover,
.nim-dialog.nim-dialog_hovered+.nim-dialog.nim-dialog_selected,
.nim-dialog:hover+.nim-dialog.nim-dialog_selected,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_selected.nim-dialog_hovered,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_selected:hover,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered+.nim-dialog.nim-dialog_selected,
.nim-dialog:not(.nim-dialog_deleted):hover+.nim-dialog.nim-dialog_selected {
    border-top: solid 1px var(--blueVKcolor)
}
.nim-dialog.nim-dialog_muted.nim-dialog_selected .nim-dialog--photo .online.mobile:after,
.nim-dialog.nim-dialog_selected .nim-dialog--photo .online.mobile:after {
    background-color: var(--blueVKcolor)
}
.im-mess-stack .im-mess-stack--content .im-mess-stack--pname a {
    color: var(--blueVKcolor)
}
.online:after,
.online.mobile:after {
    bottom: -6%;
    right: -8%
}
.nim-dialog.nim-dialog_unread .nim-dialog--unread,
.nim-dialog.nim-dialog_unread.nim-dialog_prep-injected .nim-dialog--unread {
    font-weight: 400;
    background: var(--blueVKcolor) !important
}
.nim-dialog.nim-dialog_muted.nim-dialog_selected .nim-dialog--photo,
.nim-dialog.nim-dialog_selected .nim-dialog--photo {
    border-color: var(--blueVKcolor)
}
.nim-dialog.nim-dialog_muted.nim-dialog_selected .nim-dialog--unread,
.nim-dialog.nim-dialog_selected .nim-dialog--unread {
    color: var(--blueVKcolor)
}
.box_title_wrap {
    background-color: var(--blueVKcolor)
}
.button_blue button,
.button_gray button,
.button_light_gray button,
.button_blue button,
.button_gray button,
.button_light_gray button,
.flat_button {
    background-color: var(--blueVKcolor) !important;
}

/* ARTICLE BUTTON */
.article_snippet__read_btn.flat_button {
    background-color: var(--audioDarkGrayColor) !important;
    background-image: url(https://image.ibb.co/dR0Nhx/flash.png) !important;
    background-size: 24px 24px !important;
    background-repeat: no-repeat !important;
    border-radius: 7px !important;
}
/*
.flat_button:hover {
    background-color: var(--blueVKcolor) !important;
    -webkit-filter: brightness(110%);
    -moz-filter: brightness(110%);
    -o-filter: brightness(110%);
    -ms-filter: brightness(110%);
    filter: brightness(110%)
}
*/
.button_gray button.hover,
.button_gray button:hover,
.flat_button.secondary.hover,
.flat_button.secondary:hover {
    background-color: #dfe6ed !important;
    -webkit-filter: brightness(100%);
    -moz-filter: brightness(100%);
    -o-filter: brightness(100%);
    -ms-filter: brightness(100%);
    filter: brightness(100%)
}
#page_body {
    margin-top: 46px
}
.ui_tab_sel,
.ui_tab_sel:hover,
.ui_tabs .ui_tab_sel,
.ui_tabs .ui_tab_sel:hover,
.ui_tabs_box .ui_tab_sel,
.ui_tabs_box .ui_tab_sel:hover {
