.audio_page_player .audio_page_player_next .icon,
.audio_page_player .audio_page_player_prev .icon,
.slider.slider_size_1 .slider_slide,
.wide_column .page_upload_label,
.media_selector .ms_item:before,
.no_posts_cover,
.im-page .im-page--center-empty,
.pv_like,
.post_full_like_wrap,
.ms_item:hover,
.page_module_upload.hover .page_upload_label,
a.page_module_upload:hover .page_upload_label,
.topics_module .topic_icon,
.pv_place_label,
.wall_signed_by,
