}
.top_audio_player .top_audio_player_title {
    transform: translateZ(0);
    position: absolute;
    width: auto;
    max-width: 999px;
    min-width: 220px;
    overflow: visible;
    text-overflow: clip;
    animation: scroll 5s infinite linear;
}
@-webkit-keyframes scroll {
    0%, 10% {
        -webkit-transform: translate(calc(0% + 0px), 0);
        transform: translate(calc(0% + 0px), 0)
    }
    80%, 90% {
        -webkit-transform: translate(calc(-100% + 220px), 0);
        transform: translate(calc(-100% + 220px), 0)
    }
    100% {
        -webkit-transform: translate(calc(0% + 0px), 0);
        transform: translate(calc(0% + 0px), 0)
    }
}
@-moz-keyframes scroll {
    0%, 10% {
        -moz-transform: translate(calc(0% + 0px), 0);
        transform: translate(calc(0% + 0px), 0)
    }
    80%, 90% {
        -moz-transform: translate(calc(-100% + 220px), 0);
        transform: translate(calc(-100% + 220px), 0)
    }
    100% {
        -moz-transform: translate(calc(0% + 0px), 0);
        transform: translate(calc(0% + 0px), 0)
    }
}
@keyframes scroll {
    0%, 10% {
        transform: translate(calc(0% + 0px), 0)
    }
    80%, 90% {
        transform: translate(calc(-100% + 220px), 0)
    }
    100% {
        transform: translate(calc(0% + 0px), 0)
    }
}


