css:
@-moz-document url-prefix(https://matriculaweb.unb.br/) {
.theme-blue .navbar {
    background-color: #c2dea8;
}
.sidebar .user-info {
    background: #6A9B8D;
}
.bg-blue {
    background-color: #6A9B8D !important;
}
.theme-blue .sidebar .menu .list li.active >:first-child i,
.theme-blue .sidebar .menu .list li.active >:first-child span {
    color: #6A9B8D;
}
.theme-blue .navbar-brand,
.theme-blue .navbar-brand:hover {
    color: #4B6E36;
}
.theme-blue .nav > li > a {
    color: #4B6E36;
}
.alert-info {
    background-color: #C2DDA9;
}
.alert {
    color: #4B6E36 !important;
}
.panel-primary {
    border-color: #6A9B8D;
}
.table-responsive {
    border-color: #6A9B8D !important;
}
.badge {
    background-color: #6A9B8D;
}
.col-blue {
    color: #6A9B8D !important;
}
.alert-warning {
    color: #fff !important;
}
.alert-danger {
    color: #fff !important;
}
.spinner-layer.pl-blue {
    border-color: #6A9B8D;
}
#img-unb>img {
    content: url(https://matriculaweb.unb.br/imagens/logo_unb.gif);
}
.navbar .navbar-toggle,
.theme-blue .bars {
    color: #4B6E36;
}
.info-box-4.hover-expand-effect:after {
    pointer-events: none
}
.login-page {
    background-color: #6A9B8D
}
}
