  @font-face {
    font-family: 'PT Mono';
    font-style: normal;
    font-weight: 400;
    src: local("PT Mono"), local("PTMono-Regular"), url(https://fonts.gstatic.com/s/ptmono/v5/9oRONYoBnWILk-9AnC8zMw.woff2) format("woff2");
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD; }
  * {
    font-family: 'PT Sans', sans-serif !important; }
  code {
    font-family: 'PT Mono', monospace; }
  .theme_dark #app_holder > #content .items .text,
  .theme_dark #app_holder > #content .project_history .text,
  .theme_dark #app_holder > #left_menu .expansion_panel .panel_summary h5 {
    color: rgba(255, 255, 255, 0.7); }
  #left_menu,
  #list_holder {
    width: 230px; }
  #content {
    margin-left: 249px; }
  #editor {
    width: auto !important;
    padding-right: 0; }
  #quick_find {
    margin-left: 292px; }
  .priority_1 .ist_checkbox,
  .priority_2 .ist_checkbox,
  .priority_3 .ist_checkbox,
  .priority_4 .ist_checkbox {
    background-color: transparent; }
  body .priority_1 .ist_checkbox,
  body .priority_2 .ist_checkbox,
  body .priority_3 .ist_checkbox,
  body .priority_4 .ist_checkbox {
    -webkit-border-radius: 0;
            border-radius: 0; }
  body .priority_2 .ist_checkbox,
  body .priority_3 .ist_checkbox,
  body .priority_4 .ist_checkbox {
    border-width: 1px !important;
    width: 16px;
    height: 16px; }
  .priority_1 .ist_checkbox:hover,
  .priority_2 .ist_checkbox:hover,
  .priority_3 .ist_checkbox:hover,
  .priority_4 .ist_checkbox:hover {
    background-color: transparent; }
  :not(.theme_dark) .priority_1 .ist_checkbox {
    border-color: #d1d1d1; }
  .theme_dark .priority_1 .ist_checkbox {
    border-color: #666; }
  .task_item .checker {
    padding-right: 10px; }
  .subsection_header {
    margin-right: 30px; }
  .theme_dark #app_holder > #content .subsection_header,
  .theme_dark #app_holder > #content .items .task_item td.checker,
  .theme_dark #app_holder > #content .project_history .task_item td.checker,
  .theme_dark #app_holder > #content .items .task_item .task_content_item,
  .theme_dark #app_holder > #content .project_history .task_item .task_content_item,
  .theme_dark #app_holder > #content .items .task_item .content,
  .theme_dark #app_holder > #content .project_history .task_item .content,
  .theme_dark #app_holder > #left_menu .expansion_panel {
    border-bottom-color: rgba(255, 255, 255, 0.03); }
  .agenda_item__time {
    border: 1px solid #d1d1d1;
    font-size: 11px;
    margin-right: 8px;
    min-width: inherit !important;
    padding-left: 5px; }
  #agenda_view .column_time div {
    line-height: 16px; }
  .theme_dark .agenda_item__time {
    border-color: #666; }
  #agenda_view .agenda_item__time_overdue span {
    color: #d04949 !important; }
  .project_item__color {
    -webkit-border-radius: 8px;
            border-radius: 8px;
    width: 8px;
    height: 8px;
    margin-top: 5px; }
  #projects_list .project::before,
  #projects_archived_ul .project::before,
  #ist_complete_result .project::before {
    width: 8px;
    height: 8px; }
  .generic_left_list li table td.td_color {
    min-width: 28px; }
  span.emoji {
    font-size: 12px; }
  img.emoji {
    width: 12px;
    height: 12px; }
  .project_item__name span.emoji {
    font-size: 9.6px; }
  .project_item__name img.emoji {
    width: 9.6px;
    height: 9.6px; }
  .project_shared svg {
    -webkit-transform: scale(0.8);
            transform: scale(0.8); }
  .note_content code,
  .task_content_item code {
    font-size: 12px;
    padding: 1px 4px; }
