css:
@-moz-document domain("tangthuvien.vn"){

body{
    min-width:0;
    max-width:none;
    width:100%;
    font-size:1.5rem!important;
}
.chapter .chapter-c .chapter-c-content{
    background-image: none;
}
.chapter .chapter-c{
   font-size:36px!important;
   color: #222;
}
.panel-box.panel-setting{
    font-size: 1rem!important;
}
.above_postlist, #above_postlist,.newcontent_textcontrol,.pagination_top,.above_postlist .pagination_top .pagination, .pagination span{
	float:initial;
    position:initial;
    display:initial;
    font-size:1em;
}
.pagination_top{
    width: 100%;
}
.pagination {
	display: flex!important;
    height: 2em;
    margin: 1em 0;
}
.pagination span{
    border-right:1px #c4c4c4 solid;
    border-top:1px #c4c4c4 solid;
    border-bottom:1px #c4c4c4 solid;
}
.pagination .separator{
    border-right:1px #c4c4c4 solid;
}
.pagination span:first-of-type{
    border-left:1px #c4c4c4 solid;
}
.pagination span a{
	border-width: 0 !important;
	background: none !important;
    display: inline-block!important;
    position: static!important;
    margin: 0;
    height: 100% !important;
    padding: 0 5px !important;
    font-size: 100%;
    line-height:2.2;
    min-width:2em;
    text-align:center;

}
.pagination span a.popupctrl{
    display: none!important;
}
.above_postlist .pagination_top .postpagestats,.prev_next,.postlinking .report{
	display:none!important;
}
.navtabs > ul.floatcontainer li a, .navbar_advanced_search li a{
	font-size:90%;
}
.post_thanks_button{
	line-height: 2.2!important;
}
input[type=button]{
    font-size:1em!important;
    padding:0.5em!important;
    line-height:2.2;
    min-width: 8em;
}
.left-control{
    margin-left: 0!important;
    left:2px!important;
}
.container {
    width: 100%;
	padding-right: 0;
	padding-left: 0;
    max-width: none;
}
.bg-background{
    display: none;
}
.chapter .chapter-c, .chapter .chapter-c {
    max-width: none!important;
    margin: 0 auto!important;
    font-family: initial!important;
}
.more-chap.btn{
    border: 1px solid #ccc;
    border-radius: 0;
    width: 100%;
    text-align: left;
    
}
.chapter-end{
	display:none;
}
.share-box{
 	right: auto;
	left: 0;
	bottom: 0;
	height: 45px;
}
.share-box > .contact_nct,
.share-box > .offads{
	display:none;
}
}
