@-moz-document domain("www.youtube.com") {
	  /* Theme Made By Hexy */
/* Green & Red Like Button For Youtube */
			/* V,1.0 */


/*Changing liked and disliked button color in the comments*/
	
	#comment-section-renderer .sprite-like[aria-checked=true]::before {
	    background: no-repeat url(https://dl.dropboxusercontent.com/s/wc80dff5rrzwkph/aKSNNAd.png) 0 -116px !important;
	    background-size: 324px 132px !important;
	    width: 14px !important;
	    height: 14px !important;
	    opacity: 1 !important;
	}
	#comment-section-renderer .sprite-dislike[aria-checked=true]::before {
	    background: no-repeat url(https://dl.dropboxusercontent.com/s/wc80dff5rrzwkph/aKSNNAd.png) -300px -36px !important;
	    background-size: 324px 132px !important;
	    width: 14px !important;
	    height: 14px !important;
	    opacity: 1 !important;
	}
	/*Changing like button color*/
	
	.like-button-renderer-like-button.yt-uix-button:active::before,
	.like-button-renderer-like-button.yt-uix-button.yt-uix-button-toggled::before {
	    background: no-repeat url(https://dl.dropboxusercontent.com/s/8yj972oljnl89hf/E3NuIEu.png) -339px -197px !important;
	    background-size: 435px 425px !important;
	    width: 20px !important;
	    height: 20px !important;
	}
	/*Changing dislike button color*/
	
	button.yt-uix-button.yt-uix-button-size-default.yt-uix-button-opacity.yt-uix-button-has-icon.no-icon-markup.like-button-renderer-dislike-button.like-button-renderer-dislike-button-clicked.yt-uix-button-toggled.yt-uix-post- anchor.yt-uix-tooltip .yt-uix-button-content {
	    color: #f51712!important;
	}
	dislike-button:before,
	.like-button-renderer-dislike-button:before {
	    background: no-repeat url(https://dl.dropboxusercontent.com/s/fry8mixfh76pd2f/hyrgtjrl.png) -72px -409px
	}
	.like-button-renderer-like-button:before {
	    background: no-repeat url(https://dl.dropboxusercontent.com/s/fry8mixfh76pd2f/hyrgtjrl.png) -188px -237px;
	}
	.like-button-renderer-dislike-button:before,
	.like-button-renderer-like-button:before {
	    -webkit-filter: invert(0)!important;
	    -moz-filter: invert(0)!important;
	    filter: invert(0)!important;
	}
	/*Comment like buttons*/
	
	.comment-renderer .comment-renderer-like-count.off,
	.comment-renderer.liked .comment-renderer-like-count.on {
	    margin-right: 15px !important;
	}
	.yt-badge-live,
	.yt-badge {
	    color: var(--main-color) !important;
	    padding: 0px !important;
	    background-color: transparent !important;
	}
	/*MISC*/
	
	#comment-section-renderer .comment-renderer-footer .sprite-like:hover,
	#comment-section-renderer .comment-renderer-footer .sprite-dislike:hover {
	    opacity: 1;
	}
	#comment-section-renderer .comment-renderer:hover .sprite-like,
	#comment-section-renderer .comment-renderer:hover .sprite-dislike {
	    opacity: .9;
	}
	#comment-section-renderer .comment-renderer-footer .sprite-like {
	    opacity: .7;
	}
	#comment-section-renderer .comment-renderer-footer .sprite-dislike {
	    opacity: .7;
	}
	.like-button-renderer-like-button:active .yt-uix-button-content,
	.like-button-renderer-like-button.yt-uix-button-toggled .yt-uix-button-content {
	    color: #0971C0 !important;
	}
	.like-button-renderer-dislike-button:before,
	.like-button-renderer-like-button:before {
	    -webkit-filter: invert(.85);
	    -moz-filter: invert(.85);
	    filter: invert(.85);
	}
	.like-button-renderer-like-button.yt-uix-button:active:before,
	.like-button-renderer-like-button.yt-uix-button.yt-uix-button-toggled:before {
	    -webkit-filter: invert(0)!important;
	    -moz-filter: invert(0)!important;
	    filter: invert(0)!important;
	}
}
