css:
@-moz-document domain(coub.com) {
body {
    filter: invert(100%);
    background-color: #050505;
}

div[type="image"].hero-cover,
div[type="image"].hero-cover .hero-cover__content-box,
.coub__viewer .viewer__player,
img,
.channel.channel--box-card .box-card__screen,
.story-card,
.coub__sharing .dropdown__content .sb,
.coub__sharing .dropdown__content .sb:active,
.coub__sharing__dropdown .sb.social:before,
.story__sharing-dropdown .sb.social:before {
    filter: invert(100%);
}
.coub__sharing .-st,
.fullscreen .coub__viewer .viewer__player,
.story-card__video, .story-card__img {
    filter: invert(0%)!important;
}
.object-media__img .-ribbon:hover,
.object-media__img .-ribbon:hover:active,
.sharing__dropdown__item .-ribbon:hover,
.sharing__dropdown__item .-ribbon:hover:active .story-card.-ribbon:hover,
.story-card.-ribbon:hover:active {
    box-shadow: 0px 0px 0px 3px #fccd00;
}
.coub__sharing__list .sb.vkontakte,
.story__sharing-list .sb.vkontakte,
.coub__sharing__list .sb.facebook,
.story__sharing-list .sb.facebook {
    font-size: 0px;
    line-height: 0;
    padding-right: 1px;
}

.header.-wide .header__contents {
    width: 100%;
    padding-right: 7px;
    padding-left: 7px;
}
.page__content {
    width: 100%;
}
.page__body {
    width: 100%;
	padding-left: 250px;
    margin: auto;
}
.gutter-beta--timeline>.grid-col.-col-one-third {
    width: 320px;
    margin-left: -320px;
}
.gutter-beta--timeline>.grid-col.-col-two-thirds {
    width: 100%;    
}
.coubs-list[view='list'] .coub--timeline, .coubs-list[view='listWithoutScroll'] .coub--timeline {
    width: 100%; 
    padding-right: 320px;
}

.coub-block-col {
    width: 100%;
}
.coub>.-col-two-thirds {
    width: 100%;
    padding: 0 320px 10px 250px!important;
    margin-right: -320px;
}


.adfox-banner--sticky {
	display: none;
}
}
