css:
@-moz-document regexp('(http|https)://trello\.com/.*')
{

@font-face
  {
    font-family: 'siRoboto';
    src: url('https://fonts.gstatic.com/s/roboto/v15/CWB0XYA8bzo0kSThX0UTuA.woff2') format('woff2');
  }
@font-face
  {
    font-family: 'Samim';
    src: url('https://cdn.rawgit.com/rastikerdar/samim-font/v1.0.2/dist/Samim.woff') format('woff');
  }
@font-face
  {
    font-family: 'Vazir';
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir.woff') format('woff');
  }
@font-face
  {
    font-family: 'Vazir Bold';
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v10.0.0-beta/dist/Vazir-Bold.woff') format('woff');
  }

/* ========================================= */

body {
    font-family: Samim, Roboto, "Helvetica Neue", Arial, Helvetica, sans-serif !important;
    font-size: 12px;
}

body.body-board-view {
/*     background-color: rgba(0, 61, 61, 1.0)!important; */
}

textarea {
    font-family: Samim, Roboto!important;

    direction: rtl;
}

#surface:before {
    position: fixed;
    top: 0;
    left: 0;

    width: 100%;
    height: 100%;

    content: '';

    opacity: .95;
    background-color: #ed1c24;
    background-image: url('http://unsplash.com/photos/miM9xmitmF8/download?force=true');
    background-position: center center;
    background-size: cover;

    filter: blur(0px);
}

#header, .body-board-view #header {
    z-index: 10!important;

    background: rgba(13, 13, 13, .8)!important;
    box-shadow: 10px 2px 15px rgba(0, 0, 0, .1);
}

.board-header {
    background: rgba(61, 61, 61, .7)!important;
}

.board-menu {
    position: fixed!important;
    z-index: 60!important;

    margin-top: 0!important;
}

.board-canvas {}

.list {
    border-radius: 10px!important;
}

.list-card-title {
    font-size: 13px;
    line-height: 1.7em;
}

.list-card-operation {
    right: auto;
    left: 3px;

    padding: 2px;
}

.checklist-item-details-text {
    direction: rtl;
}

.card-label.mod-card-front {
    float: right;
}

.board-wrapper.is-show-menu .board-header-btns.mod-right {
    right: -303px;

    visibility: visible;
}
  
  /******************** end of @-moz-document */

}
