css:
@-moz-document url-prefix("https://www.olx.ua/observed/search/") {
.details {width: 100%; box-sizing: border-box;}
  .details > p {display: table-row; color: #999}
  .details > p:first-child {display: table-row; color: #000}
  .details > p > span:first-child{width:150px;}
  .details > p > span:last-child{width:calc(100% - 150px);}
  .details > p > span{display:table-cell}
  
  .details table span{display: inline-block; }

  .details {width: 100%; box-sizing: border-box;}
  .details > p {display: table-row; color: #999}
  .details > p:first-child {display: table-row; color: #000}
  .details > p > span:first-child{width:150px;}
  .details > p > span:last-child{width:calc(100% - 150px);}
  .details > p > span{display:table-cell}
  
  .details table span{display: inline-block; }
  .observedview .container .wrapper{width: 1170px;}
  .usersearch-list .box{
      width: 49%;
      display: inline-block;
      box-sizing: border-box;
  }

  .usersearch-list .box table.params,
  .usersearch-list .box .details >p:not(:first-child){display:none;}
  .usersearch-list .box .alert-options li:nth-child(2){font-size: 10px;}
  .alert-options a.searchLink{color: red;}
  div.pageinfo{max-width: 100%; padding:15px 0 5px;}

/* tabs */
  #usertabs ul.tabs2{font-size: 16px; margin-top: -36px;}
  ul.tabs2 > li{float: right;}
  .paybalance-box{margin-bottom: 20px;}

/* обьвления */
  .observedview .container .wrapper{width:1070px;}

.icon.f_checkbox{
	border: 1px solid #9d9c9c;
	box-shadow: none;
}
.usersearch-list .box {
	padding: 0 20px;
}
.usersearch-list .box{
	margin: 8px 0 0;
}
.usersearch-list .box .alert-options .searchLink{
	background: #e7edee;
}
.alert-options a.searchLink {
    color: gray;
}
.alert-options a.searchLink.newAds {
    color: red;
}
.usersearch-list .box .alert-options .searchLink{
	height: auto;
}
.usersearch-list .box .details p.is-query::before {
	display:none;
}
.details > p > span:last-child{
	width:100%;
}
.offers--top,
#cookiesBar,
#mobileAppsbadge{display: none !important;}

#mainContent > div > div > section > div > div  {
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    
}
#mainContent > div > div > section > div[data-cy="observed-list"] > div > div a{
    color: #000;
}
#mainContent > div > div > section > div[data-cy="observed-list"] > div > div {
    display: inline-block;
    width: calc(50% - 1px);
    flex: 0 1 48%;
}
#mainContent > div > div > section > div[data-cy="observed-list"] > div > div > div {
    padding: 16px 20px;
}
#mainContent > div > div > section > div[data-cy="observed-list"] > div> div > div > div:last-child > p {
    text-align: center;
    border: 1px dashed #ddd;
}

#mainContent > div > div > section > div > div > div > div label p {
    font-size: 0.8rem;
}
#mainContent > div > div > section > div > div> div > div > div a{
    min-height: 3rem;
    font-size: 0.8rem;
    width: 165px;
}
#mainContent > div > div > section > div > div> div > div > div:first-child > div,
#mainContent > div > div > section > div > div> div > div > div:first-child > p:not(:nth-child(1)) {
    display:none;
}
#mainContent > div > div:first-child > div > div > div > div{
    padding: 0;
}
#mainContent > div > div > div{
    margin-bottom: 10px;
}
#mainContent > div{
    padding-top: 10px;
}
}
