css:
@-moz-document url("http://www.mcmod.cn/") {
body {
    background-color:#222222;
}
.news_block .left .name a {
    color: #bcbcbc;
}
.class_block .left .list .name a {
    background-color: #222222;
}
.class_block .left .list .name a, .class_block .right .list a {
    color: #bcbcbc;
}
div, a, ul, li {
    color: #4be;
}
.class_block .left .list .name a:hover, .class_block .right .list a:hover {
    color: #98f;
}
.news_block .right .count {
    background-color: rgba(222,222,222,0.2);
}
.news_block .right .addArea a.class:hover {
    color: #98f;
}
.news_block .right .addArea a.verify:hover {
    color: #98f;
}
.news_block .right .addArea a.edit:hover {
    color: #98f;
}
.news_block .right .editor ul a:hover {
    color: #98f;
}
.news_block .right .editor ul a {
    color: #4be;
}
.favorite_block ul li a {
    color: #bcbcbc;
}
.mod_block ul li a {
    color: #bcbcbc;
}
.topic_block .dec a {
    color: #bcbcbc;
}
.favorite_block .title .more a {
    color: #829F63;
}
.class_block .left .more a {
    color:#829F63;
}
.class_block .right .more a {
    color: #829F63;
}
.topic_block .dec span {
    color: #C3DE60;
}
.card_block ul li a {
    color: #bcbcbc;
}
.list_block .menu li a {
    color: #bcbcbc;
}
.list_block .list li a {
    color: #bcbcbc;
}
.pages_system .Pbtn, .pages_system .Pbtn_on {
    background-color: rgba(0,0,0,0)!important;
}
.pages_system .Pbtn {
    background-color: #FAFAFA;
    color: #C8A839;
}
.pages_system .Pbtn_on {
    color: #E8DF65;
}
.pages_system .Pbtn:hover {
    color: #72B434;
}
.pages_system .Pbtn_on:hover {
    color: #72B434;
}
.star_block .list li a {
    color: #C3DE60;
}
.class_block .title a {
    color: #4be;
}
.class_block .title a:hover {
    color: #C65685;
}
.class_block .left .list .name a {
    background-color: #222222;
}
.chart_block .list li a {
    color: #bcbcbc;
}
.az_block .switch li {
    color: #829F63;
}
.az_block .menu li {
    color: #bcbcbc;
}
.az_block .list li a {
    color: #bcbcbc;
}
.video_block .list li a {
    color: #bcbcbc;
}
.un_links {
    background: Transparent;
}
.un_info li strong {
    font-size: 18px;
    line-height: 60px;
    color: #89B4E1;
}
.un_info li a {
    color: #9990E5;
}
.un_info li a:hover {
    color: #C6C0F4;
}
.under {
    background:  Transparent
}
img{
    opacity:.5;
}
.top-main .info .info-icon img {
    opacity:.9;
}
.tools_box {
    background-color: #222222;
    border-top: 0px solid #DDD;
}
.tools_box .block {
    border-left: 0px solid #CCC;
    border-right: 0px solid #CCC;
}
.banner .m_menu .m_center, .banner .m_menu .m_left, .banner .m_menu .m_right {
    opacity:.75;
}
.m_center li a {
    color: #222;
}
.news_block .right .addArea a.class, .news_block .right .addArea a.mod, .news_block .right .addArea a.verify, .news_block .right .addArea a.edit {
    color: #777;
}
.class_block .left .list .name.t6 a:hover, .class_block .right .list.t6 a:hover {
    color: #808080;
}
}

@-moz-document url-prefix("http://www.mcmod.cn/history/"), url-prefix("http://www.mcmod.cn/verify/") {
.mbx {
	opacity:.75;
}
.history_center {
    background-color: #222222;
    border: 1px solid #88ada6;
}
.history_center .left .tips {
    border: 0px #233333;
    background-color: #222222;
}
.history_center .left {
    border-right: 0px solid #CCC;
}
div, a, ul, li {
    color: #bcbcbc;
}
a:visited {
    color: #70f3ff;
}
a:hover {
    color: #C65685;
}
a {
    color: #4be;
    text-decoration: none;
}
.under {
    background:  Transparent
}
.un_info li a {
    color: #9990E5;
}
.un_info li a:hover {
    color: #C6C0F4;
}
.un_info li strong {
    font-size: 18px;
    line-height: 60px;
    color: #89B4E1;
}
.history_center .right #edit_roll li {
    border-bottom: 1px dashed #88ada6;
}
.v_info {
    border: 0px solid #DDD;
    background-color: #222222;
    color: #f0f0f4;
}

}

