css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://goodgame.ru/"), domain(goodgame.ru) {
  
/* скрывает блок турниров на главной */
.main-block .big-block-wrap .tournaments-wrap {
    display: none;
}
    
/* скрывает блок видео */
.video-wrap {
    display: none;
}

/* скрывает блок марафона */
.marathon-block {
    display: none;
}
.marathon-wrap {
	margin: 0 0 0 0 !important;
}

/* фикс бага с настройками категорий стримов */
.settings-block {
    left: auto !important;
}

/* уменьшить картинки в ленте с анонсами */
/*
.feed-block .event-block img {
    max-height: 100px !important;
}
*/

/* или убрать картинки в ленте с анонсами */
.feed-block .event-block img {
    display: none;
}
.feed-block .event-block .info-block .channel-link {
    display: none !important;
}

.feed-block .event-block .info-block .announce-block .timer {
    /*display: none;*/
    font-size: unset !important;
    background-color: rgba(0,0,0,0.2) !important;
    border-radius: 5px;
    margin-top: -16px;
    margin-left: 110px;
    margin-right: 90px;
}
.feed-block .event-block .info-block {
    display: flex;
    flex-direction: column;
}
.feed-block .event-block .info-block .date {
    order: 1;
}
.feed-block .event-block .info-block .text {
    order: 3;
}
.feed-block .event-block .info-block .announce-block {
    order: 2;
}
.feed-block .event-block {
    padding-top: 2px !important;
    padding-bottom: 0px !important;
    border-bottom: 2px solid #2b3148 !important;
}
.feed-block .control-block {
    margin-bottom: 8px !important;
}


/* news - decrease font size  */
.news-wrapper .news-block p {
    font-size: 14px !important; 
    line-height: 26px !important;
    margin-bottom: 0px !important;
}
.news-wrapper .news-block h1 {
    font-size: 24px !important; 
    line-height: 32px !important;
    margin-top: 48px !important;
    margin-bottom: 20px !important;
}
.news-wrapper .news-block h2 {
    font-size: 22px !important; 
    line-height: 26px !important;
    margin-top: 22px !important;
    margin-bottom: 10px !important;
}
.news-wrapper .news-block h3 {
    font-size: 20px !important; 
    line-height: 26px !important;
    margin-top: 20px !important;
}
.news-wrapper .news-footer  {
    font-size: 14px !important; 
    line-height: 26px !important;
    margin-bottom: 0px !important;
    margin-top: 20px !important;
}
.news-text ul, ol {
    font-size: 14px !important; 
    line-height: 26px !important;
    margin-bottom: 10px !important;
    margin-top: 3px !important;
}
.news-wrapper .news-block blockquote {
    max-width: 680px !important; 
    font-size: 14px !important; 
    line-height: 26px !important;
    margin-top: 10px !important;
    margin-bottom: 10px !important;
}
.news-wrapper .news-block .news-text blockquote .quote-block .quote-author {
    display: block;
    text-align: right;
    margin-right: 20px !important;
}
.interview {
    font-size: 14px !important; 
    line-height: 26px !important;
    margin-bottom: 0px !important;
}
.news-wrapper .news-block img {
    margin-top: 18px !important;
    margin-bottom: 18px !important;
}
.news-wrapper .news-block hr {
    margin-top: 18px !important;
    margin-bottom: 18px !important;
}
.news-wrapper .news-block .media-content {
    margin-top: 18px !important;
    margin-bottom: 18px !important;
}
.news-wrapper .news-block .spoiler-block .spoiler-content {
    padding-top: 0px !important;
    /*padding-bottom: 18px !important;*/
}
.news-wrapper .news-block .news-text table tbody td {
    font-size: 14px !important; 
    line-height: 26px !important;
    margin-bottom: 0px !important;
}
.news-wrapper .news-block gg-spoiler {
    margin-bottom: 0px !important;
}
.news-wrapper .news-block .spoiler-block {
    padding: 0px 0px 18px 0px !important;
}
.news-wrapper .news-footer .btn.btn-pending {
    border: solid 2px #233056 !important;
}
.news-wrapper div.question {
    margin-bottom: 5px !important;
}
.news-wrapper div.answer {
    margin-bottom: 12px !important;
}
.news-wrapper .news-header {
    filter: brightness(0.6) blur(12px) !important;
}
.news-wrapper h1,
.news-wrapper h2,
.news-wrapper h3,
.news-wrapper .news-block .update-block,
.news-wrapper .news-block .update-block p{
    color: #dfecff;
}

.interview::before {
    left: 70px;
}

/* news - background color  */
.news-wrapper .news-block {
    background-color: #151c34;
    max-width: 930px;
    margin-top:10px;
}
.news-wrapper .news-footer {
    padding-left: 50px;
    padding-right: 50px;
    padding-bottom: 10px;
    padding-top: 20px;
    margin-top: 30px !important;
}

/* forum - decrease font size  */
.dashboard-block .title {
    font-size: 18px !important; 
    line-height: 26px !important;
}
.forum-wrap .topic-block .topic-body .title {
    font-size: 18px !important; 
    line-height: 26px !important;
}
.dashboard-block.comments-block .comment-block .content-part .comment-body {
    font-size: 14px !important; 
    line-height: 1.7em !important;
}

/* new part for forum*/
.forum-wrap .topic-block .topic-body .topic-text { /* main post text */
    font-size: 14px !important; 
    line-height: 1.7em !important;
}
.poll-block-wrap .poll-block .title-block .title { /* polls */
	font-size: 18px !important; 
    line-height: 26px !important;
}
.poll-block-wrap .poll-block .poll-question { /* polls */
	font-size: 14px !important; 
    line-height: 1.7em !important;
}
.poll-block-wrap .poll-block .poll-list .elemen { /* polls */
	font-size: 14px !important; 
    line-height: 1.7em !important;
}

/* fix left margin for view counter in news */
.news-footer .views {
    margin-left: 20px;
}

/* hide share buttons in news */
.news-wrapper .news-block .social-list {
    display: none !important;
}

/* hide news background picture */
.news-wrapper .news-header {
    background-image: unset !important;   
}
.news-wrapper .news-header::before {
    background-image: unset !important;   
}


/* скрыть блок рекомендаций в новостях */
.news-recommendations {
    visibility: hidden !important;
    height: 0px !important;
}

/* скрыть накрученные счетчики просмотров новостей на главной */
.news-element .views
{
    display: none !important;
}

/* disable spotlight */
.featured-streams-wrap 
{
    display: none !important;
}

/* spolight fixes */
.featured-streams-wrap .featured-streams-list .stream-block {
    padding: 12px !important;
}
.featured-streams-wrap > div:nth-child(1) {
	display: none !important;
}
/*
.featured-streams-wrap .featured-streams-list .stream-block.orange {
    background-color: #283249; / *#9A683C;* /
    padding-top: 5px !important;
}
.featured-streams-wrap .featured-streams-list .stream-block.green {
    background-color: #283249; /* #0e5947;* /
    padding-top: 5px !important;
}
.featured-streams-wrap .featured-streams-list .stream-block.purple {
    background-color: #283249; / *#5600a0;* /
    padding-top: 5px !important;
}
.featured-streams-wrap .featured-streams-list .stream-block .title {
    margin-bottom: 5px !important;
    font-size: 15px !important;
}
*/


/* premium buttons darker color */
.channel-wrap .channel-user-info-block .left-block .btn.btn-green {
    background-color: #404f7b;
}
.channel-wrap .channel-user-info-block .left-block .btn.btn-plus {
    background-color: #404f7b;
}


/* move galleries to the end at main page */
.main-block .small-block-wrap {
  display: flex;
  flex-flow: wrap;
}
.main-block .small-block-wrap .gallery-wrap {
  order: 1;
  width: 100%;
}


/* hide share button under channel */
.channel-content .channel-user-info-block gg-socials {
    display: none !important;
}


/* hide gif ad on main page */
.small-block-wrap gg-ads-block {
    display: none !important;
}


/* fixed color of rainbow donate button in chat */
.chat-container .chat-control-block .control-panel .donate-block:hover {
    background-color: #222e46 !important;
}

.chat-container .chat-control-block .control-panel .donate-block {
    width: 22px !important;
    overflow: hidden !important;
    white-space: nowrap !important;
    background-color: transparent !important;
    padding-top: 3px !important;
}

/* compact donate button in chat */
.chat-container .chat-control-block .control-panel .donate-block:not(.icon-plus)::before {
    content: "\e609   " !important;
    font-family: icomoon !important;
    font-size: 13px !important;
}
/* chat interative donate button */
.chat-container .chat-control-block .control-panel .donate-block.icon-plus {
    margin-right: 10px !important;
}


/* hide "funny" texts instead of deleted comments */
.comment-block > gg-comment > div > .inner-wrap > .delete-wrap {
    display: none !important;
}


/* причесываем новые комментарии */
.dashboard-block.comments-block .comment-block .content-part .comment-body p {
    font-size: 100%;
    color: #dfecff;
    margin-bottom: 8px;
}
.dashboard-block.comments-block .comment-block .content-part .comment-body h1,
.dashboard-block.comments-block .comment-block .content-part .comment-body h2,
.dashboard-block.comments-block .comment-block .content-part .comment-body h3 {
    font-size: 120%;
    line-height: 125%;
    margin-bottom: 10px;
    color: #dfecff;
}
.dashboard-block.comments-block .comment-block .content-part .comment-body ul,
.dashboard-block.comments-block .comment-block .content-part .comment-body ol {
    margin-bottom: 10px;
    margin-top: 0px;
    font-size: 100%;
}
.dashboard-block.comments-block .comment-block .content-part .comment-body ul li,
.dashboard-block.comments-block .comment-block .content-part .comment-body ol li
{
    color: #dfecff;
    line-height: 155%;
    margin-bottom: 6px;
}
.dashboard-block.comments-block .comment-block .content-part .comment-body .smile {
    margin: -5px 0 -5px;
}
.dashboard-block.comments-block .comment-block .content-part .comment-body img {
    margin-top: 4px;
}
.dashboard-block.comments-block .comment-block .content-part .comment-body .typography-block .spoiler-block {
    padding-top: 4px;
    padding-bottom: 4px;
}


/* hide "published" button in news */
.news-wrapper .news-footer .btn.btn-pending.disabled {
    display: none;
}


/* new forum topic post */
.forum-wrap.new-forum .topic-block .topic-body .topic-text p,
.gg-editor-wrapper.topic .textarea-block .textarea-editor p {
    font-size: 100%;
    color: #dfecff;
    line-height: 168%;
    margin: 8px 0;
}
.forum-wrap.new-forum .topic-block .topic-body .topic-text hr,
.gg-editor-wrapper.topic .textarea-block .textarea-editor hr {
    margin-top: 18px;
    margin-bottom: 15px;
}
.forum-wrap.new-forum .topic-block .topic-body h1,
.edit-wrap.new-topic .thread-create .title-block textarea {
    font-size: 170%;
    color: #dfecff;
    text-align: center;
    margin-top: 12px;
    margin-bottom: 15px;
}
.forum-wrap.new-forum .topic-block .topic-body h2,
.forum-wrap.new-forum .topic-block .topic-body h3 {
    color: #dfecff;
    margin-top: 12px;
}

.forum-wrap.new-forum .topic-block .topic-body .topic-text .smile,
.gg-editor-wrapper.topic .textarea-block .textarea-editor .smile {
    margin: -3px 0 -8px;
}
.forum-wrap.new-forum .topic-block .topic-body .topic-text ol li,
.forum-wrap.new-forum .topic-block .topic-body .topic-text ul li,
.gg-editor-wrapper.topic .textarea-block .textarea-editor ol li,
.gg-editor-wrapper.topic .textarea-block .textarea-editor ul li {
    color: #dfecff;
    line-height: 155%;
    margin-bottom: 8px;
}

.typography-block .textarea, .typography-block input[type="email"], .typography-block input[type="number"], .typography-block input[type="password"], .typography-block input[type="tel"], .typography-block input[type="text"], .typography-block ol, .typography-block p, .typography-block textarea, .typography-block ul {
    font-size: 100%;
}

.forum-wrap.new-forum .topic-block .topic-body {
/*     max-width: 962px; */
    background-color: #1c2644;
    padding: 1px 24px 0px 24px;
}

.forum-wrap.new-forum .topic-block .topic-footer {
    background-color: #151c34;
    padding-left: 20px;
}

.forum-wrap .topic-block .topic-footer .topic-stats {
    margin-right: 30px;
}

.forum-wrap.new-forum .topic-block .topic-body .typography-block {
    background-color: #1c2644;
    padding: 1px 24px 0px 24px;
    
}
.forum-wrap.new-forum .topic-block .topic-separator {
    background-color: #1c2644;
    margin-top: -8px;
    padding-top: 24px;
}

.forum-wrap.new-forum .topic-block .topic-footer .author-block .avatar::after {
    border-color: #1c2644 transparent transparent;
    top: -13px;
}

.forum-wrap.new-forum .topic-block .topic-start {
    max-width: 1000px;
}


/* hide hexagon */
.dashboard-block.comments-block.comments-list.sticky .dashboard-inner.comments-inner .user-avatar .hexagon {
    display: none;
}
.dashboard-block.comments-block .inner-wrap .user-avatar .custom-block .hexagon {
    display: none;
}


/* topic edit background */
.gg-editor-wrapper.topic .textarea-block .textarea-editor {
    background-color: #11172b !important;
}
.edit-wrap.new-topic .thread-create .title-block textarea {
    border-color: #1c2544;
    border-radius: 10px;
    border-width: 2px;
}



/* news gallery */
.gallery-block::after, .gallery-block::before {
    width:20px;
    top:18px;
}
.gallery-block .gallery-slider-inner .gallery-list {
    overflow: hidden;
}



