css:
@-moz-document domain("mint.intuit.com") {
/* LOGIN PAGE */
/* Hide everything but login */

main div.content,
section.mobile-app,
footer.site-footer,
div.auth-bills-signup-slider,
nav.main-menu,
button.js-auth-slider-close-x--login {
    display: none !important;
}
/* Make the login button more legible, give it space */

button.btn-simple-login {
    border-radius: 0px;
    background-color: #5F5954;
    height: 60px;
    margin-top: 20px;
    font-weight: 400;
    font-size: 16px;
}
h3.auth-form-title {
    margin-bottom: 50px;
}
form.signup-form-column:after {
    width: 0;
}
div.auth-login-wrapper {
    display: block;
}
div.auth-signup-slider {
    display: block;
    margin-left: 180px !important;
    margin-right: 180px !important;
}
form:after {
    height: 0px !important;
}
div.signup-form-column.left {
    border-right: none;
}
/* Color navbar green */

div.navbar-fixed-top {
    background: #31BB31!important;
}
/* Make top menu bottom spacing less */

.NavigationContainerView .navbar-header {
    padding-bottom: 15px
}
.CardView {
    padding: 0px;
    padding-left: 12px;
    padding-right: 12px;
    padding-top: 6px;
}
/* reduce space around account listings*/

.CardView .cardHeader {
    padding-bottom: 2px;
}
.CardView .cardContent {
    margin-top: 2px;
}
#module-accounts ul li .accounts-adv {
    visibility: hidden;
    padding: 0px;
    margin-bottom: 0px;
}
#module-accounts ul li h3 {
    line-height: 20px
}
#module-accounts ul li .accounts-list {
    margin-bottom: 0px;
}
#module-accounts ul li.moduleAccount ul li {
    padding: 6px;
}
.BillsModuleView .BillListView .BillItemView {
    padding: 5px 5px;
}
.BillsModuleView .BillListView .BillItemView .dateText {
    margin-bottom: 0px;
}
.CreditScoreMiniViewV2 .score-banner .score-number .MintNumberEase {
    font-size: 20px;
}
.CreditScoreMiniViewV2 {
    padding-bottom: 0px;
}
.open-platform-credit-score .currentScoreView .creditScoreMiniView .score-banner .score-description {
    font-size: 20px;
}
.TopFactorsList {
    display: none;
    padding-bottom: 0px;
}
.AboutYourScoreViewV2 {
    display: none;
    padding-bottom: 0px;
}
.CardView.creditScoreWidget p {
    line-height: 1.4em;
    margin: 0;
}
.CreditScoreRainbow {
    display: none;
}
.open-platform-credit-score .poweredByEquifax {
    display: none;
}
.open-platform-credit-score .creditScoreBarDiv {
    display: none;
}
.open-platform-credit-score .currentScoreView .aboutYourScoreView {
    display: none;
}
.CardView.withAFooter>footer {
    display: none;
}
.goalsWidget {
    display: none;
}
.CardView.adviceWidget {
    display: none !important;
}
.w2sWidget {
    display: none;
}
}
