css:
@-moz-document domain("www.gamedev.net") {
.ipsLayout_container {
    max-width: initial;
}
.ipsWidget, .ipsWidget.ipsWidget_horizontal, .ipsPad, .cPost .ipsComment_meta.ipsDataItem_stats,
.ipsDataItem_main, .ipsApp.ul.ipsPad, .ipsApp ol.ipsPad, .ipsPad_half, .cAuthorPane_photo,
.ipsDataList.ipsDataList_reducedSpacing .ipsDataItem_main, .ipsDataItem_icon,
.ipsLayout_sidebar, .cPost .ipsComment_meta {
    padding: 0;
}
#ipsLayout_sidebar.ipsLayout_sidebarright, .cAuthorPane {
    padding: 0 !important;
}
p {
    -webkit-margin-before: 0.5em;
    -webkit-margin-after: 0.5em;
    -webkit-margin-start: initial;
    -webkit-margin-end: initial;
}
.ipsType_normal.ipsType_richText {
    line-height: initial;
}
.ipsComment {
    margin-bottom: 5px;
}
.ipsComment [data-role="commentContent"], .cAuthorPane_photo {
    margin: initial;
}
.cAuthorPane_photo {
    transform: scale(0.75, 0.75); 
}
.cAuthorPane_info.ipsList_reset {
    vertical-align: top;
}
.cAuthorPane_author,
.cAuthorPane_info {
    line-height: 0.6em;
    font-size: 10px;
}
.ipsColumn_medium {
    width: 100px;
}
#ipsLayout_sidebar {
    min-width: 250px;
    max-width: 250px;
/*[[SIDEBAR]]*/
}
}
