css:
@-moz-document domain("gitlab.com") {
    header.navbar-gitlab-new, .global-dropdown-toggle,
    header.navbar-gitlab, .layout-nav {
        background-color: #444444 !important;
        background: none;
    }
    .badge,
    .sidebar-top-level-items>li.active .badge,
    .sidebar-top-level-items>li .badge,
    .nav-sidebar, .nav-sidebar li a, .context-header,
    header.navbar-gitlab-new .navbar-collapse,
    header.navbar-gitlab-new, .navbar-sub-nav,
    header .header-content .title,
    .global-dropdown-toggle,
    header.navbar-gitlab, .layout-nav,
    .nav-links li a {
        color: rgba(255,255,255,0.85) !important;
    }
    .badge,
    .sidebar-top-level-items>li.active .badge,
    .sidebar-top-level-items>li .badge,
    .nav-links li.active a {
        font-weight: 800 !important;
    }
    .nav-links li.active a {
        border-bottom: 4px solid #1f78d1 !important;
    }
    .scrolling-tabs-container,
    .merge-request-tabs,
    .merge-request-tabs-holder,
    .top-area, .nav-sidebar,
    .nav-links.sub-nav,
    .nav-links.sub-nav .container-fluid {
        background-color: #595959 !important;
    }
    .context-header, .nav-links {
        border-bottom: 1px solid #474747 !important;
    }
    .layout-nav {
        border-bottom: none !important;
    }
    .search form {
        background-color: rgba(167, 167, 167, 0.8) !important;
    }
    body, .content-wrapper .container-fluid {
        background: #f2f2f2 !important;
    }
    header.navbar-gitlab,
    .nav-sidebar {
        box-shadow: inset -1px 0 0 #474747 !important;
    }
}
