/*去除电视剧边框阴影*/
.c-border {
	width: 518px;
	padding: 9px;
	border: 0px solid #ffffff !important;
	border-bottom-color: #e0e0e0;
	border-right-color: #ececec;
	box-shadow: 0px 0px 0px rgba(0, 0, 0, .0) !important;
	-webkit-box-shadow: 0px 0px 0px rgba(0, 0, 0, .0) !important;
	-moz-box-shadow: 0px 0px 0px rgba(0, 0, 0, .0) !important;
	-o-box-shadow: 0px 0px 0px rgba(0, 0, 0, .0) !important;
}
.c-container {
	border: 1px solid #f7f7f7;
	/* 	border-bottom-color: #e0e0e0; */
	/* 	border-right-color: #ececec; */
	/* 	box-shadow: 1px 2px 1px rgba(0, 0, 0, .072); */
	/* 	-webkit-box-shadow: 1px 2px 1px rgba(0, 0, 0, .072); */
	/* 	-moz-box-shadow: 1px 2px 1px rgba(0, 0, 0, .072); */
	/* 	-o-box-shadow: 1px 2px 1px rgba(0, 0, 0, .072); */
	border-radius: 1px;
	padding: 15px;
	/* 	transition: all 450ms cubic-bezier(0.01, 0.48, 0.47, 0.99) 0ms; */
	/* 	background-color: #f7f7f7; */
}
.c-container:hover {
	/* 	border: 1px solid #ffffff; */
	/* 	box-shadow: 5px 5px 7px 0 rgba(0, 0, 0, 0.02); */
	background-color: #f7f7f7;
}

/* 多列外边框 */
.container {
	width: auto !important;
}
.container_l {
	width: auto !important;
}
#wrapper > br {
	display: none!important
}
.leftBlock {
	/* 	position: inherit !important; */
	height: 29px;
	line-height: 29px;
	max-height: 38px;
	break-inside: avoid;
	box-sizing: border-box;
	column-span: all;
}
.hint_toprq_tips {
	position: absolute !important;
	width: 537px;
	height: 19px;
	line-height: 19px;
}
.hint_toprq_tips_items {
	width: 444px;
	_width: 440px;
	max-height: 38px;
	position: absolute !important;
	left: 95px;
	top: 1px;
}
#rs_top_new,
.hit_top_new {
	break-inside: avoid;
	box-sizing: border-box;
	column-span: all;
	width: 538px;
	font-size: 13px;
	line-height: 1.54;
	word-wrap: break-word;
	word-break: break-all;
	margin: 0 0 14px;
}
.c-gray i {
	display: inline-block;
	width: 18px;
	height: 18px;
	vertical-align: text-bottom;
	font-style: normal;
	overflow: hidden;
	position: inherit !important;
}
#ala_img_desc {
	display: inline-block;
	background: #fff;
	margin: 0 0 .5em;
}

/* 搜索结果 */
div[id="content_left"] > div.c-container div[class*="c-gap"] {
	margin-top: 0 !important;
	margin-bottom: 0 !important;
	padding-top: 5px !important;
	padding-bottom: 5px !important;
}
}
