css:
@-moz-document domain("52pojie.cn") {
    #fl_tb {
    color: rgb(51, 51, 51)!important;
    text-shadow: rgb(255, 255, 255) 0px 1px 1px !important;
}

#style_button {
    background: -webkit-gradient(linear, 0% 0%, 0% 100%, from(rgb(238, 238, 238)), to(rgb(189, 189, 189))) !important;
    color: rgb(51, 51, 51)!important;
    display: block!important;
    font-weight: bold!important;
    height: auto!important;
    line-height: 14px!important;
    overflow: auto !important;
    padding: 8px 0px 6px 8px !important;
    text-shadow: rgb(255, 255, 255) 0px 1px 1px!important;
    width: auto!important;
}

#style_font {
    color: rgb(51, 51, 51)!important;
    display: block!important;
    font-weight: bold!important;
    height: auto!important;
    overflow: auto !important;
    text-shadow: rgb(255, 255, 255) 0px 1px 1px!important;
    width: auto!important;
}

#thread_subject {
    color: rgb(51, 51, 51)!important;
    font-weight: bold!important;
    text-shadow: rgb(255, 255, 255) 0px 1px 1px !important;
}

#threadline_7ree a {
    color: rgb(51, 51, 51)!important;
    text-shadow: rgb(255, 255, 255) 0px 1px 1px !important;
}

#um {
    color: rgb(51, 51, 51)!important;
    text-shadow: rgb(255, 255, 255) 0px 1px 1px !important;
}

#wp {
    color: rgb(51, 51, 51)!important;
    text-shadow: rgb(255, 255, 255) 0px 1px 1px !important;
}
  
code ,.gutter {
    text-shadow: none !important;
}

[title="收听TA"] {
    text-shadow: none !important;
}

*:not([class*="ico"]):not(i) {
    font-family: Tahoma,Microsoft YaHei UI!important;
}

.comiis_nav dt li {
    float: left!important;
    height: 22px!important;
    line-height: 22px!important;
    margin-right: 10px!important;
    overflow: hidden!important;
    width: auto!important;
}

.comiis_nav>ul:nth-child(-n+2) {
    float: left!important;
    height: 50px!important;
    overflow: hidden!important;
    padding-left: 58px!important;
    padding-top: 6px!important;
    width: 260px !important;
}

.comiis_nav>ul:nth-child(-n+2) li {
    float: left!important;
    height: 22px!important;
    line-height: 22px!important;
    margin-right: 13px!important;
    overflow: hidden!important;
    width: auto!important;
}

body, input, button, select, textarea {
    color: #444!important;
    /**font: 12px/1.5 Tahoma,Helvetica,SimSun,sans-serif;**/
    /**font: 13px/1.8 Chinese YeHei ;**/
    font: 14px/1.5 Tahoma,Microsoft YaHei UI!important;
    -webkit-font-smoothing: antialiased!important;
    -moz-osx-font-smoothing: grayscale!important;
}

font {
    text-shadow: rgb(255, 255, 255) 0px 1px 1px !important;
}
}
