css:
@-moz-document url-prefix(https://www.birdguides.com/sightings/), url-prefix(http://www.birdguides.com/sightings/) {
    /* Hide the sidebar */

    div.sidebar {
        display: none;
    }
    /* Hide the Birdwatch logo in the menu */

    #menu-birdwatch-logo {
        display: none;
    }
    /* Hide the advert banner in the footer */

    div.adtech-billboard {
        display: none;
    }
    /* Hide the free trial banner */

    div.free-trial-alert {
        display: none;
    }
    /* Make the main content span the full width */

    div.body-content {
        width: 100% !important;
        padding-right: 0 !important;
    }
    /* Remove the bottom margin from each sighting */

    div.sighting {
        margin-bottom: 0 !important;
    }
    /* Remove the top/bottom padding from each sighting */

    div.sighting-header, div.sighting-body {
        padding-top: 1px !important;
        padding-bottom: 1px !important;
    }
    /* Remove the bottom margin from each sighting paragraph */

    div.sighting-body p {
        margin-bottom: 0 !important;
    }
    /* Restyle the 'view sighting' button */

    div.col-lg-2.col-md-12.col-sm-12.text-right > a {
        background:none;
        border:none;
        color:#000;
        text-shadow:none;
        padding:0;
        font-size:100%;
    }
    /* Add hover effect to the 'view sighting' link */
    
    div.col-lg-2.col-md-12.col-sm-12.text-right > a:hover {
        text-decoration:underline;
    }   
    /* Make the sighting species text bold */

    div.sighting-header > div > div:nth-child(3) {
        font-weight: bold;
    }

    /* Stop the thread/camera icons from forcing a new line */
    div.sighting-body br {
        content: " ";
        display: none;
    }
}
