css:
@-moz-document domain("dgpa.gov.tw") {
.alert_msg,
.Version_btn_Div,
.Header_h2,
.f_right.color_blue,
.f_left,
.Table_Body > tr[style="background:rgba(204, 124, 236, 0.39);"] {
    display: none
}
body {
    color: #6f6e6e;
    font-size: 16px;
}
.Header_Div {
    height: auto;
    background: #f7f7f7;
    box-shadow: none;
    padding: 20px 0;
    margin-bottom: 20px;
}
.Header_YMD {
    margin: 0;
    color: #646464;
}
.Header_h1 {
    text-shadow: none;
    color: #4b4b4b;
}
#Content {
    background: #fff;
    padding: 0 20px;
}
.Content_Updata {
    float: none;
    margin: 0;
    text-align: center;
}
#Table {
    max-width: 700px;
    margin: -3em auto 0 auto;
    font-size: 1rem !important;
    border: none;
}
#Table * {
    text-align: left;
}
#Table td {
    font-size: 1rem !important;
    padding: .78571em .78571em;
    border: 1px solid #e9e9e9;
    border-top: none;
    border-collapse: collapse;
}
#Table tr:nth-child(2n) {
    background: rgb(245, 245, 245) !important;
}
#Table tr[style="background: rgba(139, 241, 138, 0.15);"] {
    background: rgb(251, 251, 251) !important;
}
#Table tr[style="background: rgba(255, 212, 85, 0.15);"] {
    background: rgb(245, 245, 245) !important;
}
#Table td:nth-child(1) {
    border-right: none;
}
#Table thead tr:nth-child(1) td,
#Table .Table_Head th {
    border: 1px solid #e9e9e9 !important;

    padding: 5px 10px;
}
#Table tbody tr[style="background: rgba(204, 124, 236, 0.39);"] {
    display: none;
}
font[color="#000080"] {
    color: #6f6e6e !important;
}
font[color="#FF0000"] {
    color: #ff7070 !important;
}
.color_Eggyolk {
    background: #f7f7f7;
    color: #606060 !important;
}
}
