css:
@-moz-document domain('fjnbnpbmkenffdnngjfgmeleoegfcffe')
{



/* general text excluding the popup from stylish chrome-extension */
#stylish-edit a,#stylish-edit h1,#stylish-edit h2,#stylish-edit label,#stylish-edit table
{
	color: rgba(240,240,240,0.8) !important;
}


/* controls */
input, button, select
{
	color: rgba(240,240,240,0.8) !important;
	background-color: rgba(255,255,255,0.1) !important;
    border-color: rgba(0,0,0,0.5) !important;
    border-style: solid !important;
    border-width: 1px !important;
    height: 22px !important;
    margin: 2px !important;
}
input:focus, button:focus, select:focus
{
    outline: #e06152 auto 5px;	
}


/*style management screen */
#stylish-manage 
{
	background-color: #2d2d2d !important;        
}
#stylish-manage h2
{
    color: rgba(240,240,240,1) !important;
}
#stylish-manage h1, #stylish-manage p, #stylish-manage label, #stylish-manage legend 
{
    color: rgba(240,240,240,0.7) !important;
}


/* main panel */
#stylish-edit 
{
	background-color: #2d2d2d !important;        
}


/*  left-panel */
#stylish-edit #header
{
	background-color: rgba(0,0,0,0.2) !important;
    border-color: #e06152 !important;
    box-shadow: 0 0 50px 0 rgba(225,20,20,0.25) !important;
}


/* editor */
.CodeMirror-line, .codemirror ,.CodeMirror
{
	background-color: rgba(50,50,50,0.85) !important;
    border-color: #e06152 !important;
    border-right-color: rgba(0,0,0,0) !important;
}
.CodeMirror:focus, .codemirror-focused
{
	outline: none !important;
    box-shadow: 0 0 50px 0 rgba(225,20,20,0.25) !important;
}
.CodeMirror-gutters 
{
    background-color: #252525 !important;
    border-color: #e06152 !important;
}


/* default settings and numbers inside editor */
.cm-s-default, .cm-number
{
	font-family: Consolas !important;
	color: #a2b4d2 !important;
    font-size: 14px !important;
}
/* .class */
.cm-qualifier
{
    color: #d09c3a !important;
}
/* #ID */
.cm-builtin
{
	color: #ff9b00 !important;	
}
/* element */
.cm-tag 
{
    color: #beef05 !important;
}
/* /*comment */
.cm-comment
{
	color: #729075 !important;
}
/* #ffb400 */
.cm-atom 
{
    color: #f1bd6d !important;
}
/* pseudo:hover */
.cm-variable-3
{
    color: #e06152 !important;
}
/* var(--this) */
.cm-variable-2
{
	color: #6498cc !important;
}
/* !important */
.cm-keyword 
{
    color: #e06152 !important;
}
/* -meta */
.cm-s-default .cm-meta 
{
    color: #59c362;
}


/* scrollbar */
::-webkit-scrollbar-track
{
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
	background-color: #e06152;
}
::-webkit-scrollbar
{
	width: 15px;
	background-color: rgba(0,0,0,0) !important;
}
::-webkit-scrollbar-thumb
{
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,.3);
	background-color: #555;
}
/* scrollbar - little resize block beneath */
.resize-grip 
{
    position: absolute;
    display: block;
    width: 15px;
    height: 8px;
    content: "  ";
    right: 0;
    bottom: 0;
    z-index: 99;
    cursor: n-resize;
    background: linear-gradient(-45deg, transparent 2px, rgba(255, 0, 0, 0.5) 2px, transparent 3px, transparent 4.5px, rgba(255, 0, 0, 0.5) 5px, #e06152 5.5px);
}


/* remove almost everything on the left panel */
#actions > div:nth-child(2), #options 
{
	display: none !important;
}
/* problems panel */
#lint
{
	opacity: 0.5 !important;
}
/* darken the problem icons in both the problem panel and the editor */
.CodeMirror-lint-marker-warning, .CodeMirror-lint-marker-error
{
    opacity: 0.5 !important;
}


/* fixes the style options menu */
#stylish-popup #menu
{
    background-color: #2a516b !important;
    box-shadow: 0 0 5px 1px rgba(0, 0, 0, 1) !important;
}
#stylish-popup #menu a
{
    padding: 3px !important;
    background-color: gray !important;
    margin: 5px !important;
    border-style: solid !important;
    border-width: 1px !important;
}



}
