css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("huffingtonpost.com"),
domain("huffingtonpost.co.uk"),
domain("huffingtonpost.ca"),
domain("huffingtonpost.fr")
{
     html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

    {
    color: #C5C8C3 !important;
    background: #232426 !important;
    }

    select, button, .comment_button, .comments_filter, .round_header, #news_column, #blogroll_wrapper, .border_for_widget, #blogroll_wrapper, .simple-headline, .reaction_pannel_v3 a, .button_mid, header, section, #curtain_wrapper

    {
    background: #1c1c1c !important;
    }


    select, input, span, code, textarea, pre, label, div, dd, option

    {
    color: #868A94 !important;
    }


    /*links*/

    a

    {
    color: #19A8E7 !important;
    }

    a:hover
    {
    color: #19A8E7 !important;
    }

    a:visited

    {
    color: #92CBC5 !important;
    }

    /*highlight*/

    textarea, pre, input, code

    {
    background: #1c1c1c !important;
    }

    #hp_bignews_pages_container, .border_for_widget

    {
    border: 0px solid #19A8E7 !important;
    }
   
}
