css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.roblox.com") {
body, #Body, html {
    background-color: #e9e9e9;
  }  
  
.forum-table-row {
  background-color: #f5f5f5;
  box-shadow: 0 1px 1px 0 rgba(25,25,25,0.3);
}
  
  body .forum-table-header, body .forum-table-header th, body .forum-table-footer, .container-footer, th {
    background-color: #0074BD;
    box-shadow: 1px 0 0 0px rgba(25,25,25,0.3);
  }  

body .forum-table-header th {
  border-left: 1px solid #eee;
  text-align: center;
}

.text-footer-nav, .text-footer-nav:link, .text-footer-nav:visited, .text-footer {
  color: #fff;
}

.text-footer-nav:hover {
  color: #000;
}

.normalTextSmall, .normalTextSmallBold, .normalTextSmaller, a.linkSmall, a.LinkSmallBold, a.linkMenuSink {
  font-size: 0.97em;
}

.btn-control.btn-control-medium, .btn-control, .btn-control:link, #forum-nav a.menuTextLink, .translate.btn-control-medium.verified-email-act.btn-control-disabled, .translate.btn-control-medium.btn-control-disabled  {
     -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    -webkit-transition: box-shadow 200ms ease-in-out;
    -o-transition: box-shadow 200ms ease-in-out;
    transition: box-shadow 200ms ease-in-out;
    border: 1px solid transparent;
    background-color: #00A2FF;
  background-image: none !important;
    border-color: #00A2FF;
    color: #fff !important;
    cursor: pointer;
    display: inline-block;
    font-weight: 400;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 4px 4px;
    font-size: 14px;
    line-height: 100%;
    border-radius: 3px;
}
  
  .translate.btn-control-medium.verified-email-act.btn-control-disabled, .translate.btn-control-medium.btn-control-disabled {
     -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    -webkit-transition: box-shadow 200ms ease-in-out;
    -o-transition: box-shadow 200ms ease-in-out;
    transition: box-shadow 200ms ease-in-out;
    border: 1px solid transparent;
    background-color: #e22d2d;
  background-image: none !important;
    border-color: #e22d2d;
    color: #fff !important;
    cursor: pointer;
    display: inline-block;
    font-weight: 400;
    height: auto;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    padding: 4px 4px;
    font-size: 14px;
    line-height: 100%;
    border-radius: 3px;
    
}


.btn-control:hover {
  -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    -webkit-transition: box-shadow 200ms ease-in-out;
    -o-transition: box-shadow 200ms ease-in-out;
    transition: box-shadow 200ms ease-in-out;
    background-color: #32B5FF !important;
    box-shadow: 0 1px 3px rgba(150,150,150,0.74);
    border-color: #32B5FF !important;
    color: #fff;
    cursor: pointer;
}
  
  .table {
    border-spacing: 0px;
  }
  
  .forum-content-background {
    border-right: 1px solid #e3e3e3;
    border-top: 1px solid #e3e3e3;
    word-wrap: break-word;
    padding-right: 5px;
    background-color: #f4f4f4;
    box-shadow: 0 2px 2px 0 rgba(25,25,25,0.3);
    
  }  

.forum-content-background table {
  background-color: transparent !important;
  margin: auto;
  width: 50%;
  
}
  
.normalTextSmaller.thread-link-container, .forum-table-row td {
    text-align: center;
  } 
 
textarea {
  background-color: White;
border-radius: 3px;
border-width: thin;
  border-style: solid;
  box-shadow: 1px 2px 2px 1px rgba(25,25,25,0.3);
  
  }
  
  input[type=text] {
    border-radius: 3px;
    border-width: thin;
  }
  
  label {
    font-weight: bold;
    margin: 3px;
  }


}
