css:
@-moz-document url-prefix("https://bittrex.com") {
body,
.content,
.login-wrapper,
.login-wrapper .box,
.panel,
.modal-content,
.modal-footer{
  background: #242d33;
	border-color: #4a565d;
}

.nav-tabs {
  border-color: #4a565d;
}

.modal-header .close {
	color: #fff;
	font-size: 35px;
	line-height: 0em;
	min-height: 21px;
	margin: 0;
}

.modal-content {
	overflow: hidden;
}
.navbar-nav>li>.dropdown-menu,
.navbar-nav>li>.dropdown-menu a,
.panel-default>.panel-heading,
.input-group .input-group-addon,
.modal-header,
.nav-tabs>li.active>a,
.nav-tabs>li.active>a:focus,
.nav-tabs>li.active>a:hover,
a.list-group-item,
.list-group-item,
.dropdown-menu,
.dropdown-menu>li>a {
	background-color: #4a565d;
	border-color: #4a565d;
	color: #adacac;
}

.dropdown-menu .divider {
	background-color: #adacac;
}

.nav-tabs>li>a:focus,
.nav-tabs>li>a:hover,
.list-group-item.active,
.list-group-item.active:focus,
.list-group-item.active:hover,
.navbar-nav>li>.dropdown-menu li:hover a,
.dropdown-menu>li>a:focus,
.dropdown-menu>li>a:hover {
	background-color: #adacac;
	border-color: #adacac;
	color: #242d33;	
}

.login-wrapper .box {
	border: 2px solid #4a565d;
}

.navbar-inverse .navbar-brand {
	height: auto;
	padding: 10px 20px;
}

.menu-wrapper {
	min-height: 0;
	background: linear-gradient(to bottom,#2c3742 0%,#28303a 100%);
	box-shadow: inset 0 -1px #101417;
	top: 48px !important;
}

.login-wrapper .logo {
        filter: invert(100%) hue-rotate(180deg);
        mix-blend-mode: screen;
}

#footmainbkg,
.footermlinkl,
.footermlinkr {
  background: #2f383d !important;
}

body, h1, h2, h3, h4, h5, .market-stats .title-none, #pad-wrapper h4, .nav-tabs a {
  color: #adacac;
}

a,
a:hover,
a:active,
a:focus {
	color: #6cbbff;
}

.login-wrapper .box input[type="text"], .login-wrapper .box input[type="password"],
.pagination>.disabled>a, .pagination>.disabled>a:focus, .pagination>.disabled>a:hover, .pagination>.disabled>span, .pagination>.disabled>span:focus, .pagination>.disabled>span:hover,
.pagination>li>a,
.pagination>li>span,
select.input-sm,
.form-control,
input[type="text"],
input[type="password"],
textarea,
.form-control[disabled],
.form-control[readonly],
fieldset[disabled] .form-control,
.menu-wrapper select,
.menu-wrapper input,
select,
input[type=number],
.input-group input,
.btn-default {
	color: #adacac !important;
	background-color: #2f383d !important;
	border: 1px solid #4a565d;
}

.pagination>.active>a {
	color: #fff !important;
	background-color: #337ab7 !important;
}

.pagination>.disabled>a,
.pagination>.disabled>a:focus,
.pagination>.disabled>a:hover,
.pagination>.disabled>span,
.pagination>.disabled>span:focus,
.pagination>.disabled>span:hover,
.form-control[disabled],
.form-control[readonly],
fieldset[disabled] .form-control {
	opacity: 0.5;
}

.pagination>li>a:focus, .pagination>li>a:hover, .pagination>li>span:focus, .pagination>li>span:hover {
	border-color: #4a565d;
  background-color: #4a565d;
	color: #fff;
}

.connected-carousels .carousel-navigation,
.connected-carousels .carousel-navigation li img {
  border-color: transparent;
	background: none;
}

.carousel-stage .img-wrapper {
	background: #242d33;
}

img.market-logo,
.carousel-stage img,
.connected-carousels .carousel-navigation li img {
	mix-blend-mode: screen;
	filter: invert(100%) hue-rotate(180deg);
}

.table-striped>tbody>tr:nth-of-type(odd) {
  background: #2f383d;
}

.table-bordered,
.table-bordered>tbody>tr>td,
.table-bordered>tbody>tr>th,
.table-bordered>tfoot>tr>td,
.table-bordered>tfoot>tr>th,
.table-bordered>thead>tr>td,
.table-bordered>thead>tr>th,
.market-stats .wrapper,
.menu-table tr td.even,
.menu-table tr td.odd {
  border-color: #4a565d;
}

.table-hover tbody tr:hover>td,
.table-hover tbody tr:hover>th,
.table-hover tbody tr.even:hover,
.table-hover tbody tr.even td.highlighted,
.table-hover tbody tr.even:hover td.sorting_1,
.table-hover tbody tr.even:hover td.sorting_2,
.table-hover tbody tr.even:hover td.sorting_3
.table-hover tbody tr.odd:hover,
.table-hover tbody tr.odd td.highlighted,
.table-hover tbody tr.odd:hover td.sorting_1,
.table-hover tbody tr.odd:hover td.sorting_2,
.table-hover tbody tr.odd:hover td.sorting_3,
.menu-table tr td:hover.even,
.menu-table tr td:hover.odd{
	background-color: #404c52;
	color: #fff;
}

.dyn-tr-add {
  opacity: 1 !important;	
}

.bs-callout-warning {
	background-color: #d9934f;
	border-color: #9e7739;
	color: #fff;
}

.bs-callout-warning h4 {
	color: #fff;
}

.bs-callout-danger {
	background-color: #d9534f;
	border-color: #9e3c39;
	color: #fff;
}

.alert-info {
	color: #fff;
	background-color: #31708f;
	border-color: #31708f;
}

.alert-info i {
  color: #fff !important;	
}

@keyframes dyn-tr-add-animation {
  80% {
		color: #fff;
  }
  0% {
    background: #404c52;
  }
}

.menu-table tr td.even,
.menu-table tr td.odd {
	background-color: transparent;
}

.market-stats .title-up,
.dyn-td-up,
.dyn-stat-up{
  color: #32ad34 !important;
	font-weight: bold;
}

.market-stats .title-down,
.dyn-td-down,
.dyn-stat-down {
  color: #ff2b2b !important;
	font-weight: bold;
}

.dyn-stat-none {
	color: #adacac !important;
}

body.Dark,
.Dark .chartContainer,
.Dark .stx-toolbar {
  background: transparent;	
}

.stx-nav,
.menuSelect li.stx-menu-divider {
	background: #4a565d !important;
}

.menuOutline,
.menuSelect ul.col {
	background-color: #2f383d;
	border-color: #4a565d;	
}
.menuSelect>li,
.menuSelect>ul>li,
.stx-dialog div.stx-heading,
.stx-dialog div.stx-data {
	color: #adacac;
}

.menuSelect>li:hover,
.menuSelect>ul>li:hover {
  background-color: #4a565d;
	color: #fff;
}

.stx-dialog {
	background-color: #2f383d;
	border-color: #4a565d;	
	color: #fff;
}

.Dark .stx-toolbar .stx-btn,
.Dark .ciqColorPicker {
	color: #adacac;
	background-color: #2f383d;
	border: 1px solid #4a565d;
}

.Dark .stx-panel-title {
	color: #adacac;
	background-color: #2f383d;
	border: 1px solid #4a565d;
}

.Dark .menuSelect.stx-line-style-menu {
  width: auto;
	right: auto;
}

#divOrderbookChart svg text{
	fill: #adacac;
}

#divOrderbookChart svg [stroke="#000000"] {
	stroke: #fff;
}

#home-wrapper .featurette > div:not(:last-child),
#home-wrapper .connected-carousels > div {
	margin-bottom: 20px;
}

#body-container {
	padding-bottom: 3em;	
}

#footmainbkg:before {
	display: block;
	position: absolute;
	right: 0;
	bottom: 100%;
	content: 'Slick Dark v1.0.1';
	white-space: pre;
	margin: 12px;
	opacity: 0.5;
}
}
