css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bing.com")
{

body .b_searchboxForm {
    background-color: #0d0000;
}
body .b_searchbox {
    background-color: inherit;
color: #FFF;
}
#sw_as .sa_as .sa_drw {
    color: #fff!important;
    background-color: black;
}
#sw_as .sa_as .sa_sg .sa_tm {
    color: #6d8ea7!important;
font-weight:800;
}
li.pp_tile.sa_hv, #sw_as .sa_hv {
    background-color: #fff;
}
.sa_as .sa_drw .sa_sg .sa_tmHS, .sa_as .sa_drw .sa_sg div.sa_tmHS strong {
    color: #999!important;
}
.sa_as .sa_tm strong {
    color: #fff!important;
}
.sa_hv, .sa_hv .sa_tm {
    text-decoration: none!important;
}
#sw_as .sa_hv {
background-color: inherit!Important;
}
button, h1, h2, h3, h4, h5, body, html, div, nav, header, footer, table, label,
 ul, li, a, img, input {color: white!important;
background-color: #000!Important;}
.b_searchboxForm .b_searchboxSubmit {
    background-color: #000!Important;
    border-color: #000!Important;
}
.b_searchboxSubmit {
border-width: 10px!Important;
border-style:solid!important;
filter: invert(100%);
}
.b_scopebar .b_active {
    border-bottom: 3px solid #fff;
}
.vr_pubinfo_mmftb:after {
background: -moz-linear-gradient(left,  rgba(0,0,0,0) 0%, rgba(255,255,255,0.2) 100%)!important;
background: -webkit-linear-gradient(left,  rgba(0,0,0,0) 0%,rgba(255,255,255,0.2) 100%)!important;
background: linear-gradient(to right,  rgba(0,0,0,0) 0%,rgba(255,255,255,0.2) 100%)!important; 
}
#sa_ul div.sa_tm:hover {
background-color: rgba(242, 242, 242, 0.19)!Important;
}
.btn.blue {
    border-color: #ddd;
}
#buttons.float_button_bar {
    border-top: 2px solid #fff;
}
.vam {
-webkit-filter: grayscale(100%);
  -moz-filter: grayscale(100%);
  -ms-filter: grayscale(100%);
  -o-filter: grayscale(100%);
  filter: grayscale(100%);
  filter: gray; /* IE 6-9 */
}
#bgDiv {
background-image: none;
}

}
