@-moz-document domain("youtube.com"), domain("apis.google.com") {
@font-face {
    font-family: 'FontAwesome';
    src: url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?v=4.7.0');
    src: url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?#iefix&v=4.7.0') format('embedded-opentype'), url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff2?v=4.7.0') format('woff2'), url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff?v=4.7.0') format('woff'), url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.ttf?v=4.7.0') format('truetype'), url('https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.svg?v=4.7.0#fontawesomeregular') format('svg');
    font-weight: normal;
    font-style: normal;
}
*::-moz-selection {
    background: #3390FF;
}
*::selection {
    background: #3390FF;
}
@keyframes spin {
    0% {
        transform: rotate(0deg);
    }
    100% {
        transform: rotate(359deg);
    }
}
@keyframes progress {
    0% {
        background-position: 40px 0;
    }
    100% {
        background-position: 0 0;
    }
}
}

@-moz-document domain("youtube.com") {
ytd-page-manager::before,
ytd-page-manager::after {
    z-index: 1000;
    position: fixed;
}
ytd-page-manager::before {
    content: '';
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: #121317;
}
ytd-page-manager::after {
    content: "BLACKTUBE ISN'T COMPATIBLE WITH THE NEW YOUTUBE DESIGN PLEASE REMOVE IT TO PREVENT BUGS FROM OCCURRING! \A\A If you want a dark design just use the new own which is integrated by YouTube iteself. \A 1. open the menu in the right top corner \A 2. Select the dark theme option \A 3. Toggle the switch to use the dark theme";
    white-space: pre;
    top: 50vh;
    left: 50%;
    width: 50%;
    transform: translate(-50%, -50%);
    color: #840707;
    font-size: 2em;
}
ytd-topbar-logo-renderer,
ytd-searchbox,
ytd-button-renderer.ytd-masthead,
ytd-notification-topbar-button-renderer {
    opacity: 0 !important;
    visibility: hidden !important;
}
ytd-active-account-header-renderer a,
ytd-compact-link-renderer,
ytd-account-settings paper-item:not(:first-of-type) {
    pointer-events: none;
    cursor: default;
}
    
}

@-moz-document regexp("https?://www.youtube.(com|([a-z]{2}))(.[a-z]{2})?."), regexp("https?://www.youtube.(com|([a-z]{2}))(.[a-z]{2})?/((\\?|feed|results|watch|live_chat|channel|c|user|playlist|subscribe_embed|upload|subscription_manager|dashboard|my_videos|view_all_playlists|index).*)") {
/* Design */
body#body {
    background: #000;
    color: #aaa;
}
a {
    color: #fff;
    text-decoration: none !important;
    transition: color .25s cubic-bezier(.25, .08, .44, 1);
}
a:hover {
    color: #fff;
}
svg:hover {
    fill: #fff;
}
