    fill: #fff;
}

/* Like Button & Option Color */
.yt-uix-button-content:before,
.no-icon-markup:before,
.yt-uix-button-icon,
#content .yt-sprite:not(.init-upload-action-icon):not(.share-service-icon):not(.studio-icon):not(.twitter-icon),
.yt-uix-dragdrop-cursor-follower .yt-sprite:not(.init-upload-action-icon):not(.share-service-icon):not(.studio-icon):not(.twitter-icon),
