@-moz-document regexp("https?://apis.google.com/u/0/(b/([0-9]+)/)?_/hovercard/.*") {
a {
    color: #aaa !important;
    transition: color .25s cubic-bezier(.25, .08, .44, 1) !important;
    text-decoration: none !important;
}
a:hover {
    color: #fff !important;
}
.nbc {
    background: #121317;
    color: #fff;
    border: none;
    border-radius: 0;
}
.eI,
.fI,
.dI {
    background: transparent !important;
    opacity: 1 !important;
}
.eI:before,
.fI:before,
.dI:before {
    font: normal normal normal 15px FontAwesome;
    color: #aaa;
    transition: color .25s cubic-bezier(.25, .08, .44, 1);
}
.eI:hover:before,
.fI:hover:before,
.dI:hover:before {
    color: #fff;
}
.dI:before {
    content: "\f2b3";
    font-size: 20px;
}
.eI:before,
.fI:before {
    content: "\f058";
    position: absolute;
    font-size: 10px;
}
.tpd {
    background: #121317;
    color: #840707;
    padding: 4px;
    border: none;
}
}

@-moz-document domain("youtube.com") {
/* HD letters in menu */
.ytp-swatch-color {
    color: #840707 !important;
}

/* Settings Button HD */
.ytp-settings-button.ytp-hd-quality-badge::after,
.ytp-settings-button.ytp-4k-quality-badge::after,
.ytp-settings-button.ytp-5k-quality-badge::after,
.ytp-settings-button.ytp-8k-quality-badge::after,
.ytp-settings-button.ytp-3d-badge::after {
    background-color: #840707 !important;
}

/* Checked item */
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
    background: #FFFFFF !important;
    background: -moz-radial-gradient(center, ellipse cover, #ffffff 0%, #ffffff 20%, #ffffff 100%) !important;
    background: -webkit-radial-gradient(center, ellipse cover, #ffffff 0%, #ffffff 20%, #ffffff 100%) !important;
    background: radial-gradient(ellipse at center, #11921A 0%, #11921A 20%, #ffffff 100%) !important;
    border-color: #ffffff !important;
    box-shadow: 0px 0px 8px #ffffff, 0px 0px 10px #ffffff, 0px 0px 12px #ffffff !important;
    content: ''
}
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox:before {
    -moz-transform: translateX(-50px);
    -ms-transform: translateX(-50px);
    -webkit-transform: translateX(-50px);
    transform: translateX(-50px)
}
.ytp-big-mode .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox:before {
    -moz-transform: translateX(-50px);
    -ms-transform: translateX(-50px);
    -webkit-transform: translateX(-50px);
    transform: translateX(-50px)
}

/* Unchecked item */
.ytp-menuitem[aria-checked="false"] .ytp-menuitem-toggle-checkbox {
    background: #FFFFFF !important;
    background: -moz-radial-gradient(center, ellipse cover, #ffffff 0%, #ffffff 20%, #ffffff 100%) !important;
    background: -webkit-radial-gradient(center, ellipse cover, #ffffff 0%, #ffffff 20%, #ffffff 100%) !important;
    background: radial-gradient(ellipse at center, #121317 0%, #121317 20%, #ffffff 100%) !important;
    box-shadow: 0px 0px 6px #ffffff, 0px 0px 8px #ffffff, 0px 0px 10px #ffffff !important;
    content: '';
    background-image: none;
}
}

@-moz-document domain("youtube.com") {
