    background: -moz-linear-gradient(left, rgb(132, 7, 7) 0%, rgb(132, 7, 7) 28%, rgb(132, 7, 7)66%, rgb(132, 7, 7) 100%);
    background: -webkit-linear-gradient(left, rgb(132, 7, 7) 0%, rgb(132, 7, 7) 28%, rgb(132, 7, 7) 66%, rgb(132, 7, 7) 100%);
