css:
@-moz-document domain("an1dub.name"), domain("tr.anidub.com") {

#slider_box, #header_b, .speedbar {
  display: none;
}

.wrap {
	width: auto !important;
    margin: 0 40px !important;
}

.banner {
    display: none;
}
  
#content section {
    width: auto !important;
}
  
.story {
    float: left;
    width: 98%;
    margin-right: 10px;
    position: relative;
}


@media only screen and (min-width:1350px) {
    .story {
    	width: 48%;
    }
}

@media only screen and (min-width:1775px) {
    .story {
    	width: 32%;
    }
}

.story.full {
	float: none;
	width: auto;
}

.story:nth-child(3n+2) {
	margin-right: 0;
}

.story_h {
	height: 50px;
    overflow: hidden;
}
  
.story_h .rcol {
	position: absolute;
	bottom: 10px;
	left: 10px;
}

.story_c {
	height: 250px;
    overflow: auto;
}

.story.full .story_c {
	height: auto;
}
  
.tags {
	display: none !important;
}

.torrent {
	float: left;
    width: 50%;
}
  
.story_h {
	width: auto !important;
}
  
.story_h > div.lcol h2, .story_h > div.lcol h1 {
	max-width: 9999px !important;
}

.story .story_inf {
    width: auto !important;
    height: 22px;
}

.story .story_inf li {
    padding: 0 3px !important;
}
}
