css:
@-moz-document domain('developer.android.com') {
html, body,
body[layout=docs] .devsite-article,
body[theme=android-theme][layout=docs],
body[theme=android-theme] [ds-is=header] .devsite-top-logo-row,
body[theme=android-theme] [ds-is=header] [ds-is=tabs].upper-tabs .devsite-tabs-wrapper,
devsite-book-nav .devsite-nav-list > .devsite-nav-item.devsite-nav-heading:not(.devsite-nav-accordion) .devsite-nav-title,
devsite-language-selector>devsite-select .devsite-select-toggle,
devsite-select .devsite-select-toggle,
devsite-footer-promos, devsite-footer-linkboxes, devsite-footer-utility {
    background: #000;
    color: #aaa;
}

body[theme=android-theme][layout=docs],
body[theme=android-theme] .devsite-book-nav-bg::after,
body[ready] .devsite-book-nav-bg[fixed] {
    background: #111;
}

/*new*/
h1 {color: inherit;}
tr {background:#313131;}

.devsite-version-selector-select {background:#333; color: #fff; border: none;}
select:hover,
select:focus {background:#444;}

.devsite-future-version, .devsite-future-version * {color:rgba(68,68,68,.75);}

.devsite-landing-row:not([background]) {background-color:#090909;}

.dac-flat-button,
.devsite-landing-row-item h3,
.devsite-landing-row-item-description-content, 
.devsite-landing-row:not([background]):not([foreground]) .devsite-landing-row-description {color:inherit;}

body[theme=android-theme] devsite-tabs.lower-tabs a { background:#4C7728; }

body[theme=android-theme] devsite-header devsite-tabs.upper-tabs .devsite-tabs-wrapper,
body[theme=android-theme] devsite-header .devsite-top-logo-row { background: #000; }

devsite-header .devsite-header-upper-tabs,
devsite-tabs tab[overflow-tab] .devsite-tabs-overflow-menu,
body[theme=android-theme] devsite-tabs.upper-tabs>div>tab>a:hover, 
body[theme=android-theme] devsite-tabs.upper-tabs>div>tab>a:focus,
body devsite-tabs tab[overflow-tab] .devsite-tabs-overflow-menu tab a,
body devsite-tabs tab[overflow-tab] .devsite-tabs-overflow-menu tab a:hover,
body devsite-tabs tab[overflow-tab] .devsite-tabs-overflow-menu tab a:focus { background:#000; color: #bababa; }

devsite-search .devsite-searchbox,
devsite-select .devsite-select-list,
devsite-search[search-active] .devsite-searchbox:hover,
devsite-search[search-active] .devsite-searchbox:focus,
devsite-search[search-expanded] .devsite-searchbox:hover,
devsite-search[search-expanded] .devsite-searchbox:focus,
devsite-language-selector>devsite-select .devsite-select-toggle,
body[theme=android-theme] devsite-header devsite-search[search-active] .devsite-search-field:hover,
body[theme=android-theme] devsite-header devsite-search[search-active] .devsite-search-field:focus,
body[theme=android-theme] devsite-header devsite-search .devsite-search-field:hover,
body[theme=android-theme] devsite-header devsite-search .devsite-search-field:focus,
body[theme=android-theme] devsite-header devsite-search .devsite-search-field { background: #212121; color:#fff;}

body[theme=android-theme] devsite-header .devsite-product-description-row .devsite-breadcrumb-link:hover,
body[theme=android-theme] devsite-header .devsite-product-description-row .devsite-breadcrumb-link:focus,
body[theme=android-theme] devsite-header .devsite-product-description-row .devsite-breadcrumb-guillemet:hover,
body[theme=android-theme] devsite-header .devsite-product-description-row .devsite-breadcrumb-guillemet:focus { color:#000; }

body[theme=android-theme] devsite-tabs.upper-tabs tab>a,
body[theme=android-theme] devsite-tabs.upper-tabs>div>tab[active]>a { color: #aaa; }

body[theme=android-theme] devsite-tabs.lower-tabs tab[active]>a { color: #ddd; }

body[theme=android-theme] devsite-tabs.lower-tabs a:hover,
body[theme=android-theme] devsite-tabs.lower-tabs a:focus { color:#a9a9a9; }

devsite-select .devsite-select-list,
devsite-select .devsite-select-toggle {border: none;}

body[theme=android-theme] devsite-header devsite-search .devsite-searchbox::before,
body[theme=android-theme] devsite-header devsite-search-background {background: #000;}

devsite-select .devsite-select-item:hover,
devsite-select .devsite-select-item:focus,
devsite-select .devsite-select-toggle:hover,
devsite-select .devsite-select-toggle:focus {background: #292929;}


body[theme=android-theme] devsite-tabs.lower-tabs a,
body[theme=android-theme] devsite-header .devsite-product-description-row .devsite-breadcrumb-link {color: #000;}

body[theme=android-theme] .devsite-collapsible-section,
body[theme=android-theme] [ds-is=tabs].lower-tabs a {
    background: #4C7728; /* dat android green */
}

body[theme=android-theme] [ds-is=tabs].lower-tabs tab[active]>a {
    color: #F3FFFF;
}
body[theme=android-theme] [ds-is=tabs].lower-tabs a {
    color: #C6D3A3;
}
body[theme=android-theme] [ds-is=tabs].upper-tabs>div>tab[active]>a{
    color: #CAD6A7;
}

body[theme=android-theme] [ds-is=tabs].upper-tabs>div>tab>a:focus, 
body[theme=android-theme] [ds-is=tabs].upper-tabs>div>tab>a:hover {
    color: #fff;
}

body[theme=android-theme] [ds-is=header] .devsite-product-description-row .devsite-breadcrumb-guillemet, 
body[theme=android-theme] [ds-is=header] .devsite-product-description-row .devsite-breadcrumb-link {
    color: #000;
}

body[theme=android-theme] [ds-is=header] .devsite-product-description-row .devsite-breadcrumb-guillemet:focus, 
body[theme=android-theme] [ds-is=header] .devsite-product-description-row .devsite-breadcrumb-guillemet:hover, 
body[theme=android-theme] [ds-is=header] .devsite-product-description-row .devsite-breadcrumb-link:focus, 
body[theme=android-theme] [ds-is=header] .devsite-product-description-row .devsite-breadcrumb-link:hover {
    color: #fff;
}


body[theme=android-theme] [ds-is=header] [ds-is=search] .devsite-search-field,
body[theme=android-theme] [ds-is=header] [ds-is=search] .devsite-search-field:hover,
body[theme=android-theme] [ds-is=header] [ds-is=search] .devsite-searchbox::before, 
body[theme=android-theme] [ds-is=header] [ds-is=search][search-active] .devsite-search-field, 
body[theme=android-theme] [ds-is=header] [ds-is=search][search-active] .devsite-search-field:hover,
body[theme=android-theme] [ds-is=header] devsite-search-background,
devsite-search .devsite-popout-result,
[ds-is=search] .devsite-searchbox {
    background: #333;
}

devsite-footer-linkboxes .devsite-footer-linkbox-heading,
body[theme=android-theme] [ds-is=header] [ds-is=search][search-active] .devsite-search-field, 
body[theme=android-theme] [ds-is=header] [ds-is=search][search-active] .devsite-search-field:hover {
    color: #fff;
}

devsite-footer-linkboxes .devsite-footer-linkbox-link,
devsite-footer-promos .devsite-footer-promo-title,
devsite-footer-utility .devsite-footer-sites-link,
devsite-footer-utility .devsite-footer-utility-link,
.devsite-nav-accordion>devsite-expandable-nav>.devsite-nav-title,
.devsite-nav-title {
    color: #aaa;
}
.devsite-nav-heading>.devsite-nav-title {
    color: #cfcfcf;
}

devsite-selector { 
    background: #2b2b2b; 
    border: 1px solid #444;
}

devsite-selector devsite-tabs[connected] {
    border-bottom: 1px solid #444;
}

/* API Reference pages */
.api {color: inherit;}
.api-level a, .api-level a:active, .api-level a:visited { color: #fff; }
tr.api { border-top: 1px solid #444;}

/* .api-signature {background: #2b2b2b;} */

#jd-content devsite-heading > h1 { color: #fff; }

td, td code, table.responsive th code, table.responsive td code, .jd-inheritance-table tr td {
    color: inherit;
}

.jd-sumtable-expando td {
    background: #222;
    border: 1px solid #333;
    color: #aaa;
}

table.responsive tr:not(.alt) td:first-child, table.inhtable>tbody>tr>td, td.jd-linkcol, .jd-descrcol {
    background: #222;
    color: inherit;
}

/* Notes and Warnings */
.note, .note :link, .note :visited, .note code, 
.special, .special :link, .special :visited, .special code, 
aside, aside :link, aside :visited, aside code, aside var {
    background: #121212;
    color: inherit;
}

aside :link, aside :visited, .note :link, .note :visited, .special :link, .special :visited {
    background: inherit !important;
}

.caution, .caution :link, .caution :visited, .caution code { background: #121212; }
.caution a:focus, .caution a:hover { background: inherit; text-decoration: underline; }

/* Code Boxes */
pre { background: #2b2b2b; color: #aaa; }
code { background: none; color: #A9B7C6; }

devsite-code pre .pln, devsite-code pre .pun { color: #A9B7C6; }
devsite-code pre .str, devsite-code pre .com { color: #698651; }
devsite-code pre .kwd { color: #CC7832; }
devsite-code pre .tag { color: #E8BF6A; }
devsite-code pre .atn { color: #9876AA; }
devsite-code pre .atv { color: #69864C; }
devsite-code pre .typ { color: #CC7832; }
devsite-code pre .var { color: #9876AA; }
devsite-code pre .lit { color: #BBB529; }

table.responsive td tr:not(.alt) td:first-child, table.responsive tr:not(.alt) td td:first-child, td { background: #000; }
.gs-webResult .gs-snippet, .gs-imageResult .gs-snippet, .gs-fileFormatType { color:#999; }
}
