css:
@-moz-document domain(web.whatsapp.com) {
  .emoji:not(important), .emojik:not(important)
  {
      --ml-no-invert:true!important;
	  --ml-backgroundimage:Image!important;
  }

  .emoji-panel-section:not(important)
  {
      overflow-y: hidden!important;
  }

  .menu-tabs-marker:not(important)
  {
      --ml-background-background-color:Border!important;
  }

  .icon-msg-dblcheck-ack:not(important)
  {
      filter:hue-rotate(calc(var(--ml-invert) * 180deg)) invert(var(--ml-invert))!important;
  }
  
  .emoji-panel-body:not(impt)::-webkit-scrollbar { width: 6px!important; }

  #window:not(impt)
  {
      background-color:white!important;
      --ml-ignore:true!important;
  }
}
