css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("developer.mozilla.org") {
    
#document-main,
#content div.center.clear {
    background: #000;
}
#newsletter-box,
#helpful-survey,
pre[class*="language-"],
pre[class*="language-"] *,
pre.syntaxbox,
.toc,
#wiki-left,
#quick-links,
#quick-links .title,
#main-nav,
#main-header,
#main-header .center {
    background: #0f0f0f !important;
}
table,
tbody,
tr,
th,
td,
.text-content table.standard-table:not(.learn-box) td {
    border-color: #0f0f0f !important;
    box-shadow: none !important;
}
main#content {
    background-color: #000;
}
body {
    color: #888;
}
.quick-links a {
    color: #777;
}
.newsletter-box {
    background-color: #222;
}
ul.tags li {
    background-color: #333;
    border-color: #222;
}
button.helpful-survey-vote {
    background-color: #333;
}
.button,
button {
    background-color: #333;
}
.submenu {
    background-color: #222;
    color: #888;
}
.title {
    color: #888;
}
li.nav-main-item a {
    color: #999 !important;
}
a.logo {
    filter: invert(79%);
}
div#wiki-left {
    box-sizing: border-box;
    padding: 10px;
}
input#newsletterEmailInput {
    background-color: #333;
    color: #999;
}
.home-masthead {
    background-color: #353c42;
    background-image: none;
}
input {
    background-color: #444 !important;
    color: #999 !important;
}
a.cta-link {
    filter: brightness(3);
}
.home-masthead:before {
    display: none;
}
form#newsletterForm {
    background-color: #222;
}
.column-callout.callout-saucelabs {
    background-color: #24272d;
}
p {
    color: #888;
}
.home-hacks {
    background-color: #24272d;
}










li.nav-main-item a {
    transition-duration: 0.2s;
}
li.nav-main-item a:hover {
    background-color: #555;
    color: #999 !important;
}
#main-header .center {
    border-color: #777;
}
input#main-q {
    border: 0px;
}
ul.card-grid li {
    background-color: #222 !important;
    box-shadow: none !important;
}
.overheadIndicator.communitybox {
    background-color: #222;
}
button:hover {
    background-color: #222 !important;
}
body .topicpage-table .section:first-of-type,
body .topicpage-table td:first-of-type,
body .topicpage-table th:first-of-type {
    border-color: #666;
}
.overheadIndicator,
.notice.experimental,
div.note,
.draft,
.note,
.notice,
.prefixBox,
.privilegedBadge,
.renamed,
.secureContexts,
.todo,
.translationInProgress,
.warning.warning-review {
    background-color: #222;
    border-color: #61611e;
}
div.warning {
    background-color: #222;
    border-color: #8c2525;
}
div.warning {
    background-color: #222;
    border-color: #8c2525;
}
th {
    color: #222;
}
.htab>div {
    background-color: #222;
}
li.selected {
    background-color: #222 !important;
}
.htab>ul>li.selected {
    background-color: #222 !important;
}
.htab>ul>li {
    background-color: #333 !important;
}
.htab>ul>li.selected a {
    color: #777;
}
table.compat-table td.header,
table.compat-table th {
    color: #888;
}
table.compat-table td {
    background-color: transparent;
}
.draft,
.note,
.notice,
.prefixBox,
.privilegedBadge,
.renamed,
.secureContexts,
.todo,
.translationInProgress,
.warning.warning-review {
    background-color: #333;
}
    
}
