css:
@-moz-document domain("instapaper.com") {
.title_row a.article_title {
    font-family: Adobe Arabic;
    font-weight: bold;
    font-size: 30px;
}
.article_preview {
    font-family: Adobe Arabic;
    font-size: 24px;
    max-width: 125rem;
}
.thumbnails .article_image {
    display: none;
}
.thumbnails .article_text_meta.article_has_image {
    padding-right: 1px
}
.search_mode .article_item {
    font-family: Adobe Arabic;
    font-size: 30px;
    color: red;
}
.titlebar h1 {
    font-family: Adobe Arabic;
    font-weight: bold;
    line-height: 1em;
}
.lyon_mode #story,
.lyon_mode #footer,
.lyon_mode #speedRead {
    font-family: Adobe Arabic;
    font-size: 30px;
}
.story {
    line-height: 1.4em !important;
}
p {
    margin: 0 0 1.4em !important
}
.highlight_item .title_row a.article_title {
    font-family: Adobe Arabic;
    font-weight: bold;
    font-size: 24px;
}
.highlight_item .article_preview {
    font-family: Adobe Arabic;
    font-size: 24px;
}
blockquote {
    padding: 10px 20px;
    margin: 10px 0;
    font-size: 26px;
    border-left: 5px solid grey
}
}
