css:
@-moz-document domain("www.youtube.com") {
/* DEV-ALPH */

/* Play-Progress */
.html5-play-progress,
.ytp-play-progress {
    background: #167fec !important;
}

/* Progess-Dot */
.html5-scrubber-button:hover,
.ytp-scrubber-button:hover,
.ytp-swatch-background-color,
.ytp-swatch-background-color-secondary {
    background: #167fec !important;
}

/* Quality */
.ytp-settings-button.ytp-hd-quality-badge::after,
.ytp-settings-button.ytp-4k-quality-badge::after,
.ytp-settings-button.ytp-5k-quality-badge::after,
.ytp-settings-button.ytp-8k-quality-badge::after,
.ytp-settings-button.ytp-3d-badge::after {
    background-color: #167fec !important;
}

/* Quality-Option */
.ytp-swatch-color {
    color: #167fec !important;
}

/* Addnota */
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
    background: #167fec !important;
}

/* Time */
.ytp-time-duration,
.ytp-time-current {
    color: #88BDFF;
}

/*Bufor*/
.html5-load-progress,
.ytp-load-progress {
    background: #88BDFF !important;
}

/*Play/Stop/Next/Full */
.ytp-playlist-menu-button-icon .ytp-svg-fill,
.ytp-chrome-top .ytp-button:not(.ytp-playlist-menu-button) .ytp-svg-fill,
.ytp-chrome-controls .ytp-button .ytp-svg-fill {
    fill: #88BDFF;
    /* NEW */
    /* NEW */
    /* NEW */
}
}
