}
100% {
background-image: url(https://i.imgur.com/P3iIICm.png);
}
    }
@keyframes Pictobeat
{
  0%
  {
    height:142%;
  }

  80%
  {
    height:142%;
  }

  83%
  {
    height:135%;
  }

  100%
  {
    height:142%;
  }
}
@keyframes Corona
{
  0%
  {
    transform: scale(1);
  }

  80%
  {
    transform: scale(1);
  }

  83%
  {
    transform: scale(0.6);
  }

  100%
  {
    transform: scale(1);
  }
}
@keyframes Transizionecoverbanners
{
  0%
  {
    opacity:0.2;
  }

  40%
  {
    opacity:0.5;
  }

  43%
  {
    opacity:0.5;
  }

  100%
  {
    opacity:0.5;
  }
}
@keyframes Coachbeat
{
  0%
  {
    filter: brightness(100%);
    transform: scale(1);top:-10%
  }

  40%
  {
    filter: brightness(100%);
    transform: scale(1);top:-10%
  }

  43%
  {
    filter: brightness(150%);
    transform: scale(0.95);top:-8.5%
  }

  100%
  {
    filter: brightness(100%);
    transform: scale(1);top:-10%
  }
}
