css:
@-moz-document domain("google")
{
}

@-moz-document regexp("https://(www|books|encrypted|accounts|translate).google.(com|co.[a-z]{2}|com.[a-z]{2}|[a-z]{2})?/(?!maps|doodles|advanced|history|preferences|intl|services|work|wallet|fonts).*")
{
  #lst-ib
  {
    background: transparent!important;
  }

  *
  {
    text-shadow: 0 0.05em #000,0 -0.05em #000,0.05em 0 #000,-0.05em 0 #000!important;
    box-shadow: none!important;
  }

  html:not(#ׂ)
  {
    background: url(https://i.imgur.com/W1yoiw7.gif)#000000!important;
    min-height: 100%!important;
  }

  :not(#ׂ)
  {
    color: #FFF!important;
  }

  :not(#ׂ) :link
  {
    color: #99CCFF!important;
  }

  :not(#ׂ) :visited
  {
    color: #A0A0A0!important;
  }

  ::selection
  {
    background-color: #FFF!important;
    color: #000!important;
    text-shadow: none!important;
  }

  ::-moz-selection
  {
    background-color: #FFF!important;
    color: #000!important;
    text-shadow: none!important;
  }

  ._Bs,._LSh,#irc_pc,.mn-dwn-arw
  {
    border-color: #FFF transparent!important;
  }

  .tnv-lt-arw
  {
    border-color: transparent #FFF!important;
  }

  .goog-flat-menu-button-dropdown
  {
    border-color: #FFF transparent!important;
  }

  .gb_Wa,.gb_Xa
  {
    border-color: transparent transparent #000!important;
  }

  #sfdiv
  {
    border-color: #FFF;
  }

  #sfdiv
  {
    border-right: 1px solid #FFF!important;
  }

  #sfdiv.sbhcn
  {
    border-color: #A0A0A0!important;
  }

  #sfdiv.sbfcn
  {
    border-color: #4D90FE!important;
  }

  :not(#sfdiv):not(.mn-dwn-arw):not(._Bs):not(.goog-flat-menu-button-dropdown):not(#gt-src-wrap):not(
#gt-res-wrap):not([type="email"]):not([type="password"])
  {
    border: none!important;
  }

  :not([title]),[title^="Results"]
  {
    background-color: transparent!important;
  }

  [role="listbox"]:not(#ׂ),[aria-hidden="false"]:not(#ׂ),[style*="z-index: 2000"]:not(#ׂ),[role="menu"]:not(#hdtb-more),
[aria-label="Account Information"]:not(#ׂ),[id^="gbd"]:not(#ׂ),[class$="dlg"]:not(#ׂ),#fsett:not(#ׂ),
#ab_options:not(#ׂ),.crp:not(#ׂ),.hdtb-mn-o:not(#ׂ),.ab_dropdown:not(#ׂ),.popupContent:not(#ׂ),.jfk-scrollbar:not(#ׂ)
  {
    background-color: #000!important;
  }

  .sfbgg,.card-mask
  {
    background-image: none!important;
  }

  .ab_button,[role="button"]:not(#ׂ),[type="submit"],[type="button"],.goog-flat-menu-button:not(#ׂ)
  {
    background: transparent!important;
  }

  body.hp #hplogo,body.hp #fbar,body.hp #prm-pt
  {
    display: none!important;
  }

  body.hp #lga
  {
    background: url(https://www.google.com/images/branding/googlelogo/2x/googlelogo_light_color_272x92dp.png
)no-repeat center 109px!important;
    background-size: 272px 92px!important;
  }

  #logo>*,#logocont>*
  {
    opacity: 0!important;
  }

  #logo,#logocont
  {
    background: url(https://www.google.com/images/branding/googlelogo/2x/googlelogo_light_color_272x92dp.png
)no-repeat center!important;
    background-size: 92px 30px!important;
  }

  .gb_b.gb_Nb,.jfk-button-img,.ab_button:not([href="#"])>span,.csb,._Zgf
  {
    filter: brightness(0)invert(1);
    -webkit-filter: brightness(0)invert(1);
  }

  #abar_button_opt
  {
    width: 26px!important;
  }

  .ab_ctl
  {
    margin-left: 4px!important;
  }

  .gb_Nb,.kbtn-small:not(.selected)
  {
    opacity: .667!important;
  }

  .gb_Nb:hover,.kbtn-small:hover
  {
    opacity: 1!important;
  }

  .ab_button
  {
    min-width: 26px!important;
  }
}
