css:
@-moz-document domain("edstem.org") {
/* Login page */
   .start-container {
       background-color: rgba(40, 38, 38, 0.85);
   }

   .toast {
       box-shadow: 0 5px 20px rgba(0, 0, 0, 0.1);
       background-clip: padding-box;
   }

   .toast.error {
       background-color: #ffb9a9;
       border-color: rgba(0, 0, 0, 0.1);
   }

   .toast.error .toast-body {
       color: #430c00;
   }

   .toast.warning {
       background-color: #ffe6bd;
       border-color: rgba(0, 0, 0, 0.1);
   }

   .toast.warning .toast-icon {
       fill: #573600;
       stroke: none;
   }

   .toast.warning .toast-body {
       color: #573600;
   }

   .toast.success {
       background-color: #95ff95;
       border-color: rgba(0, 0, 0, 0.1);
   }

   .toast.success .toast-icon {
       fill: #002f00;
       stroke: none;
   }

   .toast.success .toast-body {
       color: #002f00;
   }

   .toast.info {
       background-color: white;
       border-color: rgba(0, 0, 0, 0.1);
   }

   .toast.info .toast-icon {
       fill: #0d309a;
       stroke: none;
   }

   .toast.info .toast-body {
       color: #0d309a;
   }

   .toast-icon {
       flex: 0 0 auto;
       margin-right: 10px;
       font-size: 15px;
   }

   .toast-body {
       flex: 1 0 0px;
       color: #666;
   }

   .toast-body > * + * {
       margin-top: 5px;
   }

   .toast-title {
       font-size: 16px;
       font-weight: bold;
       line-height: 1.2em;
   }

   .toast-text {
       font-size: 14px;
       line-height: 1.2em;
   }

   .appbar {
       position: fixed;
       left: 0;
       right: 0;
       top: 0;
       display: flex;
       height: 50px;
       background-color: #78508c;
       z-index: 1000;
       color: rgba(255, 255, 255, 0.8);
   }

   .appbar-group.title {
       flex: 1 1 0px;
       white-space: nowrap;
       overflow: hidden;
       font-size: 20px;
       display: flex;
       align-items: center;
       color: white;
   }

   .appbar-group.sections + .appbar-group.user::before {
       background-color: rgba(255, 255, 255, 0.25);
   }

   .appbar-btn {
       color: inherit;
       display: flex;
       align-items: center;
       justify-content: center;
       background-color: transparent;
       border: none;
       min-width: 50px;
       padding: 0;
       cursor: pointer;
       position: relative;
   }

   .appbar-btn:hover {
       text-decoration: none;
   }

   .appbar-btn:not(.ed) {
       opacity: 0.75;
   }

   .appbar-btn:not(.ed)::before {
       background-color: rgba(255, 255, 255, 0.2);
   }

   .appbar-btn:not(.ed).router-link-active::before {
       transform: scale(1);
       opacity: 1;
       border-radius: 0;
   }

   .appbar-btn.ed {
       padding: 0 20px;
   }

   .appbar-btn.ed .appbar-icon {
       font-size: 30px;
   }

   .appbar-btn.router-link-active,
   .appbar-btn.active,
   .appbar-btn:hover {
       opacity: 1;
   }

   .appbar-icon {
       font-size: 25px;
       fill: white;
       stroke: none;
   }

   .sparkle.appbar-sparkle {
       position: absolute;
       left: 0;
       top: 0;
       right: 0;
       bottom: 0;
   }

   .navbar.appbar-small {
       position: fixed;
       left: 0;
       top: 0;
       right: 0;
       z-index: 1000;
       box-shadow: 0 1px 0 rgba(0, 0, 0, 0.1);
       background-color: #78508c;
       fill: white;
       stroke: white;
       color: white;
   }

   .navbar.appbar-small.appbar-small-small .navbar-title {
       font-size: 14px;
       font-weight: normal;
   }

   .navbar.appbar-small.v-enter-active,
   .navbar.appbar-small.v-leave-active {
       transition: transform 0.1s ease-in-out;
   }

   .navbar.appbar-small.v-enter,
   .navbar.appbar-small.v-leave-active {
       transform: translateY(-100%);
   }

   .navbar-button.abp-action {
       fill: inherit;
       stroke: inherit;
       color: inherit;
   }

   .navbar-button.abp-action.abp-action-hamburger {
       position: relative;
       background-color: inherit;
   }

